INSERT INTO `post` (`id`, `cate_id`, `title`, `content`, `description`) VALUES
(1, 2, 'TUYỂN SINH DU HỌC NHẬT BẢN', '<h2>ĐĂNG K&Yacute; DU HỌC NHẬT BẢN TẠI B&Igrave;NH ĐỊNH UY T&Iacute;N VỚI KOKONO</h2>\r\n\r\n<p><strong><img alt=\"Du học Nhật Bản ở Nghệ An là con đường khó khăn thử thách nhưng cũng đầy thú vị.\" src=\"http://duhockokono.vn/pic/Service/images/Kokono%20chu.jpg\" style=\"height:17px; width:72px\" /><em>&nbsp;</em></strong><strong><a href=\"http://duhockokono.vn/dang-ky-du-hoc-nhat-ban-tai-nghe-an.htm\">Đăng k&yacute; du học Nhật Bản tại&nbsp;</a>B&igrave;nh Định li&ecirc;n tục tuyển sinh cho kỳ học th&aacute;ng 1 năm 2018 tại Nhật Bản.</strong><strong>&nbsp;Đ&atilde; chắp c&aacute;nh cho rất&nbsp; nhiều thế hệ học vi&ecirc;n đến với đất nước Nhật Bản xinh đẹp để l&agrave;m việc v&agrave; học tập. Kokono tự h&agrave;o sẽ đem đến cho bạn 1 lộ tr&igrave;nh du học &quot;chuẩn&quot; nhất với chi ph&iacute; chỉ từ&nbsp;<em>180 triệu.</em>&nbsp;Đ&acirc;y kh&ocirc;ng phải con số nhỏ nhưng đầu tư cho tương lai th&igrave; v&ocirc; c&ugrave;ng xứng đ&aacute;ng. Sẽ chẳng bao giờ c&oacute; đ&iacute;ch đến nếu như bạn kh&ocirc;ng chọn cho m&igrave;nh một con đường. H&atilde;y chọn&nbsp;<em>Du học Nhật Bản Kokono tại B&igrave;nh Định</em>&nbsp;- nơi khởi đầu giấc mơ của bạn!!!</strong></p>\r\n\r\n<p><strong><em>Du học Nhật Bản - con đường mới cho bạn!!!</em></strong></p>\r\n\r\n<p>&nbsp;<img alt=\"du học Nhật Phú Yên\" src=\"http://duhockokono.vn/pic/Service/images/585e46c3cb11b227491c3377.png\" style=\"height:30px; width:30px\" />Bạn ở B&igrave;nh Định, bạn y&ecirc;u th&iacute;ch đất nước Nhật Bản&nbsp;v&agrave; bạn đang t&igrave;m kiếm&nbsp;<strong>C&ocirc;ng ty tư vấn du học Nhật Bản uy t&iacute;n</strong>&nbsp;để được nghe những th&ocirc;ng tin hữu &iacute;ch về du học Nhật Bản.&nbsp;<strong>C&ocirc;ng ty tư vấn du học Nhật Bản tại B&igrave;nh Định&nbsp;</strong>đang l&agrave; điểm đến uy t&iacute;n d&agrave;nh cho c&aacute;c bậc phụ huynh v&agrave; c&aacute;c bạn trẻ c&oacute; nhu cầu Du học Nhật Bản - vừa học vừa l&agrave;m<strong>.&nbsp;</strong>Với 39 chi nh&aacute;nh tr&ecirc;n to&agrave;n quốc, c&ugrave;ng với đội ngũ c&aacute;n bộ quản l&yacute; năng động, nhiệt t&igrave;nh, chuy&ecirc;n nghiệp v&agrave; c&aacute;c tư vấn du học gi&agrave;u kinh nghiệm, ch&uacute;ng t&ocirc;i đ&atilde; đem đến cơ hội học tập v&agrave; l&agrave;m việc cho h&agrave;ng trăm du học sinh trong cả nước n&oacute;i chung v&agrave; du học sinh tại Ph&uacute; Y&ecirc;n n&oacute;i ri&ecirc;ng.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Du học Nhật Bản Bình Định\" src=\"http://duhockokono.vn/pic/Service/images/nhung-cau-noi-hay-ve-thanh-cong-2(1).jpg\" style=\"height:411px; width:450px\" /></p>\r\n\r\n<h3><img alt=\"Du học Nhật Bản Thái Bình\" src=\"http://duhockokono.vn/pic/Service/images/chitay.png\" style=\"height:30px; width:30px\" /><strong>&nbsp;</strong><em><strong>&nbsp;TẠI SAO N&Ecirc;N CHỌN DU HỌC TẠI NHẬT BẢN???</strong></em></h3>\r\n\r\n<p><strong>&ndash;</strong>&nbsp;<strong>Nhật Bản&nbsp;</strong>l&agrave; một đất nước nhiều danh lam thắng cảnh đẹp với nền văn h&oacute;a đậm chất &Aacute; Đ&ocirc;ng, mang đến sự gần gũi, dễ h&ograve;a nhập hơn l&agrave; c&aacute;c đất nước Ch&acirc;u &Acirc;u, ch&acirc;u Mỹ.</p>\r\n\r\n<p><strong>-&nbsp;</strong><strong>&nbsp;</strong><strong>Nhật Bản</strong>&nbsp;c&oacute;&nbsp;<strong>nền gi&aacute;o dục chất lượng h&agrave;ng đầu thế giới</strong>, bằng quốc tế được c&ocirc;ng nhận to&agrave;n cầu.</p>\r\n\r\n<p><strong>&ndash;&nbsp;Chi ph&iacute; du học Nhật Bản rẻ h</strong><strong>ơn</strong>&nbsp;rất nhiều so với c&aacute;c nền gi&aacute;o dục tương đương như Anh, &Uacute;c, Mỹ&hellip;</p>\r\n\r\n<p><strong>&ndash; Ph&aacute;p luật Nhật Bản</strong>&nbsp;cho ph&eacute;p du học sinh&nbsp;<strong>đi l&agrave;m th&ecirc;m để trang trải học ph&iacute;</strong>&nbsp;v&agrave; n&acirc;ng cao ng&ocirc;n ngữvới những c&ocirc;ng việc kh&ocirc;ng qu&aacute; vất vả, kh&ocirc;ng ảnh hưởng đến thời gian học tập mang lại thu nhập kh&aacute; cao.</p>\r\n\r\n<p><strong>&ndash;&nbsp;</strong>Ngo&agrave;i việc được t&iacute;ch lũy kiến thức ở nền gi&aacute;o dục ho&agrave;n hảo&nbsp;bạn c&ograve;n<strong>&nbsp;học hỏi được đức t&iacute;nh cần c&ugrave;, si&ecirc;ng năng, nghi&ecirc;m t&uacute;c trong c&ocirc;ng việc</strong>&nbsp;&ndash; những yếu tố gi&uacute;p bạn th&agrave;nh c&ocirc;ng trong tương lai của m&igrave;nh.</p>\r\n\r\n<p><strong>&ndash;&nbsp;</strong>Với con số&nbsp;<strong>80% sinh vi&ecirc;n Việt Nam</strong>&nbsp;ở lại Nhật l&agrave;m việc sau khi tốt nghiệp&nbsp;đ&atilde; n&oacute;i l&ecirc;n rằng bạn ho&agrave;n to&agrave;n c&oacute; cơ hội ở lại Nhật l&agrave;m việc.</p>\r\n\r\n<p><strong>&ndash;&nbsp;</strong>Hiện nay c&aacute;c c&ocirc;ng ty Nhật Bản được đ&aacute;nh gi&aacute; l&agrave;&nbsp;<strong>trả lương cao nhất tại Việt Nam</strong>.&nbsp;Sau khi du học về, kh&ocirc;ng kh&oacute; khăn g&igrave; để bạn c&oacute; được c&ocirc;ng việc&nbsp;với mức thu nhập cao trong c&aacute;c c&ocirc;ng ty, tập đo&agrave;n lớn của Nhật tại Việt Nam.</p>\r\n\r\n<p><em><strong>TẠI SAO N&Ecirc;N CHỌN KOKONO L&Agrave;M ĐỊA CHỈ TƯ VẤN DU HỌC NHẬT BẢN CHO BẠN</strong></em></p>\r\n\r\n<p>-&nbsp;Học tiếng Nhật tại&nbsp;<strong>Kokono B&igrave;nh Định&nbsp;</strong>trước khi đi du học Nhật Bản 2017 tại&nbsp;<strong>C&ocirc;ng ty du học Nhật Bản Kokono B&igrave;nh Định</strong>. C&oacute; chỗ ở&nbsp;cho học sinh ở xa.<br />\r\n- Được tư vấn v&agrave; chọn trường học khi nộp hồ sơ.<br />\r\n- Được giới thiệu học bổng khen thưởng đối với c&aacute;c bạn học sinh c&oacute; th&agrave;nh t&iacute;ch học tập v&agrave; tu dưỡng tốt.<br />\r\n- Hỗ trợ c&aacute;c thủ tục vay vốn ng&acirc;n h&agrave;ng.<br />\r\n-&nbsp;Học tiếng Nhật từ 4 đến 6 th&aacute;ng do gi&aacute;o vi&ecirc;n Nhật kết hợp với gi&aacute;o vi&ecirc;n Việt Nam c&oacute; chuy&ecirc;n m&ocirc;n cao trực tiếp đ&agrave;o tạo, giảng dạy tại Trung t&acirc;m Nhật Ngữ.<strong>&nbsp;</strong><br />\r\n-&nbsp; Trung t&acirc;m đ&agrave;o tạo Nhật ngữ của ch&uacute;ng t&ocirc;i được trang bị ph&ograve;ng ốc tho&aacute;ng m&aacute;t, sạch sẽ, hiện đại, đầy đủ tiện nghi phục vụ tối đa cho việc sinh hoạt, học tập của học vi&ecirc;n.&nbsp;<strong>Phương ph&aacute;p giảng dạy độc đ&aacute;o, theo s&aacute;t thực tiễn v&agrave; ch&uacute; trọng hiệu quả đ&agrave;o tạo gi&uacute;p học vi&ecirc;n n&acirc;ng cao năng lực Tiếng Nhật v&agrave; r&egrave;n được những kỹ năng mềm cần thiết để th&iacute;ch nghi tốt hơn với cuộc sống tại Nhật sau n&agrave;y.&nbsp;</strong><br />\r\n-&nbsp;&nbsp;<strong>Kokono B&igrave;nh Định</strong>&nbsp;phối hợp với nh&agrave; trường xin việc cho du học sinh sau khi nhập cảnh từ 1 tuần &ndash; 1 th&aacute;ng t&ugrave;y theo tr&igrave;nh độ tiếng Nhật của du học sinh.&nbsp;<strong>C&aacute;c em sẽ c&oacute; việc l&agrave;m trong thời hạn tối đa l&agrave; 1 th&aacute;ng sau khi sang Nhật.</strong></p>\r\n\r\n<p><img alt=\"Đăng ký du học Nhật Bản ở Bình Định\" src=\"http://duhockokono.vn/pic/Service/images/binh%20d%E1%BB%8Bnh.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<h3>Nhu cầu du học Nhật Bản tại B&igrave;nh Định</h3>\r\n\r\n<p>C&oacute; thể n&oacute;i, được đặt ch&acirc;n đến xứ mặt trời mọc l&agrave; mong ước của rất nhiều người. Ng&agrave;y nay, với nhiều ch&iacute;nh s&aacute;ch x&uacute;c tiến gi&aacute;o dục quốc tế, con đường đi&nbsp;<strong>du học Nhật Bản</strong>&nbsp;đ&atilde; trải d&agrave;i v&agrave; dễ d&agrave;ng hơn bao giờ hết. &nbsp;Điều kiện cũng rất thuận lợi, bất cứ học sinh, sinh vi&ecirc;n n&agrave;o c&oacute; nhu cầu đều c&oacute; thể đăng k&yacute; đi&nbsp;<strong>du học tại Nhật Bản</strong>. Tại&nbsp;<strong>B&igrave;nh Định</strong>, theo một khảo s&aacute;t nhỏ m&agrave; Kokono đ&atilde; thực hiện, rất đ&ocirc;ng học sinh, sinh vi&ecirc;n tại đ&acirc;y đều c&oacute; nhu cầu đi du học v&agrave; địa điểm m&agrave; họ muốn tới ch&iacute;nh l&agrave;&nbsp;<strong>Nhật Bản</strong>. Con số sinh vi&ecirc;n&nbsp;&nbsp;<strong>B&igrave;nh Định</strong>,<strong>&nbsp;</strong>tại<strong>&nbsp;Nhật Bản</strong>&nbsp;ng&agrave;y c&agrave;ng tăng trong những năm gần đ&acirc;y.</p>\r\n\r\n<h2>Đăng k&yacute; du học Nhật Bản tại B&igrave;nh Định như thế n&agrave;o?</h2>\r\n\r\n<p>Hầu hết c&aacute;c trường học tại&nbsp;<strong>Nhật Bản</strong>&nbsp;kh&ocirc;ng tiếp nhận hồ sơ trực tiếp từ ph&iacute;a du học sinh nếu kh&ocirc;ng c&oacute; c&aacute; nh&acirc;n hay đơn vị n&agrave;o uy t&iacute;n chịu tr&aacute;ch nhiệm li&ecirc;n hệ v&agrave; bảo l&atilde;nh cho họ. Hơn nữa, việc tự l&agrave;m hồ sơ, tự chọn trường khiến bạn mất nhiều thời gian do sự kh&aacute;c biệt ng&ocirc;n ngữ v&agrave; y&ecirc;u cầu thủ tục. V&igrave; vậy, kết quả đạt Visa thường kh&ocirc;ng cao, đồng thời th&ocirc;ng tin m&agrave; bạn biết về c&aacute;c trường thường kh&aacute; sơ s&agrave;i. Bởi vậy, bạn n&ecirc;n&nbsp;t&igrave;m&nbsp;đến một địa chỉ uy t&iacute;n để nhờ tư vấn v&agrave;&nbsp;<strong>đăng k&yacute; du học Nhật.</strong></p>\r\n\r\n<p><br />\r\nVới nhu cầu ng&agrave;y c&agrave;ng cao,&nbsp;&nbsp;<strong>B&igrave;nh Định&nbsp;</strong>l&agrave; nơi tập trung rất&nbsp;<strong>nhiều trung t&acirc;m tư vấn du học</strong>&nbsp;uy t&iacute;n v&agrave; lu&ocirc;n sẵn s&agrave;ng hỗ trợ bạn mọi thủ tục để&nbsp;<strong>du học Nhật Bản</strong>&nbsp;th&agrave;nh c&ocirc;ng. Qua nhiều năm khẳng định năng lực, Kokono vinh dự nằm trong top c&ocirc;ng ty tư vấn được&nbsp;<a href=\"http://duhockokono.vn/truong-du-hoc-nhat.htm\" target=\"_blank\">c&aacute;c trường Nhật ngữ&nbsp;</a>tin tưởng lựa chọn. Với hơn 39 chi nh&aacute;nh tr&ecirc;n to&agrave;n quốc, ch&uacute;ng t&ocirc;i lu&ocirc;n nỗ lực v&agrave; kh&ocirc;ng ngừng ph&aacute;t triển mỗi ng&agrave;y.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Đăng ký du học Nhật Bản ở Bình Định\" src=\"http://duhockokono.vn/pic/Service/images/binh%20dih%203.jpg\" style=\"height:338px; width:450px\" /><br />\r\n<em>Kokono nguyện l&agrave;m cầu nối gi&uacute;p chặng đường&nbsp;<strong>du học Nhật Bản&nbsp;</strong>của học sinh&nbsp;</em>&nbsp;<strong>B&igrave;nh Định</strong>,<em>&nbsp;trở n&ecirc;n dễ d&agrave;ng nhất.</em><br />\r\n&nbsp;</p>\r\n\r\n<h2>Điều kiện du học Nhật Bản ở B&igrave;nh Định</h2>\r\n\r\n<p><em><strong>Để trở th&agrave;nh du học sinh Nhật Bản ngay tại B&igrave;nh Định bạn cần đ&aacute;p ứng được những điều kiện sau:</strong></em></p>\r\n\r\n<p><em><strong>Điều kiện cơ bản:&nbsp;</strong></em>Học sinh, sinh vi&ecirc;n tr&ecirc;n địa b&agrave;n&nbsp;<strong>B&igrave;nh Định</strong>&nbsp;đ&atilde; tốt nghiệp THPT, Trung Cấp, Cao đẳng, Đại học mong muốn đi Du học Nhật Bản; Tuổi kh&ocirc;ng qu&aacute; 38; Nếu đ&atilde; từng đi tu nghiệp sinh/ thực tập sinh tại Nhật Bản phải c&oacute; giấy chứng nhận của nơi l&agrave;m việc.<br />\r\n<br />\r\nNgo&agrave;i những điều kiện cơ bản như tr&ecirc;n, du học sinh cần đạt một số điều kiện kh&aacute;c như chứng minh t&agrave;i ch&iacute;nh, tr&igrave;nh độ tiếng Nhật... Mọi thắc mắc về c&aacute;c điều kiện n&agrave;y, vui l&ograve;ng gọi Hotline:<strong>&nbsp;0913.828.222 - 0989.212.668</strong>, hoặc đăng k&yacute; theo mẫu th&ocirc;ng tin cuối b&agrave;i để được&nbsp;<strong>tư vấn ho&agrave;n to&agrave;n miễn ph&iacute;.</strong></p>\r\n\r\n<h3>Chi ph&iacute; du học Nhật Bản tại B&igrave;nh Định</h3>\r\n\r\n<p><strong>Chi ph&iacute; du học Nhật Bản ở B&igrave;nh Định&nbsp;</strong>từ&nbsp;<strong>180 triệu - 220 triệu</strong>,&nbsp;t&ugrave;y thuộc v&agrave;o trường Nhật ngữ bạn đăng k&yacute;. Trong đ&oacute; bao gồm:&nbsp;<em>Học ph&iacute; 1 năm, k&yacute; t&uacute;c x&aacute; 3 - 6 th&aacute;ng, chi ph&iacute; xử l&yacute; hồ sơ, chi ph&iacute; dịch thuật, c&ocirc;ng chứng,&nbsp;<a href=\"http://duhockokono.vn/thu-tuc-xin-visa-du-hoc-nhat-ban.htm\" target=\"_blank\">thủ tục visa.</a></em><br />\r\n<br />\r\nC&aacute;c khoản ph&iacute; li&ecirc;n quan đến thủ tục hồ sơ tại Việt Nam l&agrave; cố định. C&aacute;c khoản&nbsp;<a href=\"http://duhockokono.vn/danh-sach-cac-truong-nhat-ngu.htm\" target=\"_blank\">học ph&iacute;</a>&nbsp;v&agrave; k&yacute; t&uacute;c x&aacute;, gia đ&igrave;nh sẽ trực tiếp chuyển khoản v&agrave;o t&agrave;i khoản của trường.&nbsp;<strong>Kokono cam kết kh&ocirc;ng thu th&ecirc;m bất kỳ khoản ph&iacute; n&agrave;o ngo&agrave;i hợp đồng.</strong></p>\r\n\r\n<p><em><strong>KOKONO B&Igrave;NH ĐỊNH CAM KẾT</strong></em><br />\r\n<br />\r\n<strong>- LỘ TR&Igrave;NH Đ&Oacute;NG PH&Iacute; MINH BẠCH R&Otilde; R&Agrave;NG.<br />\r\n- MIỄN PH&Iacute; TƯ VẤN.<br />\r\n- TỶ&nbsp;LỆ&nbsp;ĐỖ VISA 99%.<br />\r\n- C&Oacute; VIỆC L&Agrave;M TH&Ecirc;M NGAY KHI SANG NHẬT<br />\r\n- HỖ TRỢ DU HỌC SINH TRONG SUỐT QU&Aacute; TR&Igrave;NH SỐNG V&Agrave; HỌC TẬP TẠI NHẬT BẢN.</strong></p>\r\n\r\n<p><img alt=\"Làm thêm khi đi du học Nhật Bản có khó không?\" src=\"http://duhockokono.vn/pic/Service/images/binh%20dinh%202.jpg\" style=\"height:338px; width:450px\" /><br />\r\n<em>&nbsp;Việc l&agrave;m th&ecirc;m khi đi du học Nhật l&agrave; th&aacute;ch thức cũng l&agrave; cơ hội cho bạn ho&agrave;n thiện m&igrave;nh!</em><br />\r\n&nbsp;</p>\r\n\r\n<p>Hơn nữa, đi du học Nhật, c&aacute;c bạn ở<em><strong>&nbsp;B&igrave;nh Định&nbsp;</strong></em>sẽ được nh&agrave; trường giới thiệu&nbsp;<a href=\"http://duhockokono.vn/dieu-kiem-lam-them-khi-du-hoc-nhat-ban.htm\" target=\"_blank\">việc l&agrave;m th&ecirc;m</a>. C&aacute;c c&ocirc;ng việc l&agrave;m th&ecirc;m nhẹ nh&agrave;ng như: sắp cơm hộp, b&aacute;n h&agrave;ng, phục vụ qu&aacute;n ăn...đem lại cho bạn những khoản thu nhập kha kh&aacute; gi&uacute;p bạn trang trải học ph&iacute;, chi ph&iacute; sinh hoạt, giảm bớt nỗi lo về t&agrave;i ch&iacute;nh v&agrave; trang bị th&ecirc;m nhiều trải nghiệm th&uacute; vị. Th&ocirc;ng thường, gia đ&igrave;nh chỉ phải hỗ trợ nhiều nhất chi ph&iacute; ban đầu, sau đ&oacute;, sinh vi&ecirc;n sẽ tự đứng tr&ecirc;n ch&iacute;nh đ&ocirc;i ch&acirc;n của m&igrave;nh. Như vậy,<em><strong>&nbsp;du học Nhật Bản</strong></em>&nbsp;đối với con em&nbsp;<strong><em>B&igrave;nh Định&nbsp;</em></strong>l&agrave; sự đầu tư đ&aacute;ng gi&aacute; cho tương lai. L&agrave; cơ hội để r&egrave;n luyện bản lĩnh tr&ecirc;n đất Nhật. Bạn chắc chắn sẽ thay đổi một c&aacute;ch đ&aacute;ng ngạc nhi&ecirc;n.<br />\r\n<br />\r\nH&atilde;y đến c&ugrave;ng Kokono để biết biết th&ecirc;m nhiều th&ocirc;ng tin bổ &iacute;ch về&nbsp;<strong><em>du học Nhật Bản tại B&igrave;nh Định,</em></strong>&nbsp;cũng như y&ecirc;n t&acirc;m hơn trong qu&aacute; tr&igrave;nh đăng k&yacute; du học. Gọi ngay hotline:&nbsp;<strong>0913.828.222 - 0989.212.668</strong>&nbsp;hoặc đăng k&yacute; theo mẫu th&ocirc;ng tin cuối b&agrave;i để được&nbsp;<strong>tư vấn ho&agrave;n to&agrave;n miễn ph&iacute;.<br />\r\n<br />\r\nH&atilde;y để Kokono chắp c&aacute;nh cho ước mơ du học của bạn! Ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ phụ huynh v&agrave; học sinh!</strong></p>\r\n\r\n<p><em><strong>B&iacute; quyết của th&agrave;nh c&ocirc;ng l&agrave; h&atilde;y bắt đầu. B&iacute; quyết để bắt đầu l&agrave; chia nhỏ c&aacute;c c&ocirc;ng việc nặng nề, phức tạp th&agrave;nh những việc nhỏ dễ quản l&yacute; hơn, rồi bắt đầu với việc thứ nhất.</strong></em></p>\r\n\r\n<p><strong>Mời đăng &yacute; tư vấn MIỄN PH&Iacute; theo mẫu cuối b&agrave;i!<br />\r\nCh&uacute;ng t&ocirc;i sẽ gọi lại trong v&ograve;ng 5 ph&uacute;t!</strong><br />\r\n<strong>&nbsp;</strong><br />\r\n<img alt=\"Du học Nhật Bản, Du học Hàn Quốc Kokono\" src=\"http://duhockokono.vn/pic/Service/images/Du%20h%E1%BB%8Dc%20Nh%E1%BA%ADt%20B%E1%BA%A3n%2C%20H%C3%A0n%20Qu%E1%BB%91c%20Kokono%201.jpg\" style=\"height:229px; width:500px\" /></p>\r\n\r\n<p><strong><em>Btv L&ecirc; Th&uacute;y</em></strong></p>\r\n', ''),
(2, 2, 'DỊCH VỤ LÀM VISA TRỌN GÓI', '<p><strong>Dịch vụ l&agrave;m Visa trọn g&oacute;i<em>&nbsp;(Visa Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ)&nbsp;</em>được cung cấp bởi C&ocirc;ng ty Cổ phần Tư vấn Du học Kokono. Qua 10 năm ho&agrave;n thiện Visa cho du học sinh tr&ecirc;n cả nước, Kokono mong muốn d&agrave;nh những kinh nghiệm qu&yacute; b&aacute;u đ&atilde; t&iacute;ch lũy được trong suốt thời gian qua gi&uacute;p bạn lấy được&nbsp;<em>Visa du lịch, Visa c&ocirc;ng t&aacute;c, Visa du học, Visa Tu nghiệp sinh (Visa xuất khẩu lao động)</em>&nbsp;một c&aacute;ch nhanh ch&oacute;ng v&agrave; thuận tiện nhất. Đến với Kokono, bạn h&atilde;y d&agrave;nh thời gian cho việc chuẩn bị h&agrave;nh tr&igrave;nh sắp tới, việc lấy Visa Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ h&atilde;y để ch&uacute;ng t&ocirc;i lo!</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Xin visa Nhat Trung Han Anh Uc My - Kokono 3\" src=\"http://duhockokono.vn/pic/Service/images/Xin%20visa%20Nhat%20Trung%20Han%20Anh%20Uc%20My%20-%20Kokono%203(1).jpg\" /></p>\r\n\r\n<p><br />\r\nViệc tự&nbsp;<em><strong>l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ&nbsp;</strong></em>m&agrave; kh&ocirc;ng sử dụng&nbsp;<strong>dịch vụ l&agrave;m VISA&nbsp;</strong>sẽ v&ocirc; c&ugrave;ng kh&oacute; khăn v&agrave; nguy cơ bị trượt rất cao. Đặc biệt trong trường hợp, bạn kh&ocirc;ng c&oacute; thời gian, tr&igrave;nh độ tiếng Anh kh&ocirc;ng ổn, kh&ocirc;ng biết tiếng v&agrave; kh&ocirc;ng biết thủ tục l&agrave;m Visa tới đất nước đ&oacute;. Mọi thứ trở n&ecirc;n ngo&agrave;i tầm với. B&ecirc;n cạnh đ&oacute;, c&aacute;c điều kiện xin Visa từ việc chuẩn bị hồ sơ, chứng minh t&agrave;i ch&iacute;nh, bảo l&atilde;nh&hellip;đều y&ecirc;u cầu khắt khe v&agrave; khiến bạn mệt mỏi v&agrave; &quot;stress&quot;.&nbsp;<em>Bởi vậy, h&atilde;y giảm thiểu rủi ro bằng c&aacute;ch lựa chọn một đơn vị uy t&iacute;n v&agrave; d&agrave;y dặn kinh nghiệm để chuyến đi được tiết kiệm, an to&agrave;n v&agrave; thoải m&aacute;i nhất.&nbsp;</em><strong>Để l&agrave;m Dịch vụ VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ trọn g&oacute;i</strong>&nbsp;vui l&ograve;ng gọi hotline:&nbsp;<em><strong>0912.916.322&nbsp;</strong></em>để ho&agrave;n thiện hồ sơ một c&aacute;ch dễ d&agrave;ng, nhanh ch&oacute;ng v&agrave; thuận lợi nhất!</p>\r\n\r\n<h3>Bạn c&oacute; thể tự l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ kh&ocirc;ng?</h3>\r\n\r\n<p>C&acirc;u trả lời l&agrave; c&oacute;. Bạn ho&agrave;n to&agrave;n c&oacute; thể tự&nbsp;<em><strong>l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ</strong></em>. Tuy nhi&ecirc;n, nếu kh&ocirc;ng nắm r&otilde; thu tục hồ sơ xin VISA, bạn sẽ tốn rất nhiều c&ocirc;ng sức, thời gian, tiền bạc m&agrave; đ&ocirc;i khi kh&ocirc;ng đạt được kết quả. Nếu bị từ chối VISA, bạn vừa mất cơ hội du lịch hoặc l&agrave;m việc, vừa g&acirc;y ra những l&atilde;ng ph&iacute; v&agrave; tốn k&eacute;m kh&ocirc;ng cần thiết. Bởi vậy, để kh&ocirc;ng phải lo lắng về quy tr&igrave;nh, thủ tục, kh&ocirc;ng cần mất qu&aacute; nhiều thời gian nghi&ecirc;n cứu, kh&ocirc;ng lo hồ sơ bị trả về...h&atilde;y để Kokono gi&uacute;p bạn!<br />\r\n<br />\r\n<strong>&gt;&gt; Đăng k&yacute; tư vấn l&agrave;m Visa miễn ph&iacute; qua hotline:</strong><br />\r\n<br />\r\n<strong><img alt=\"Du học Nhật Bản năm 2018 - Kokono \" src=\"http://duhockokono.vn/pic/Service/images/Hotline%20Du%20hoc%20Kokono.png\" /></strong><strong>___</strong><strong>Hotline:&nbsp;</strong><em><strong>0912.916.322</strong></em></p>\r\n\r\n<h3><img alt=\"Xin visa Nhat Trung Han Anh Uc My - Kokono\" src=\"http://duhockokono.vn/pic/Service/images/Xin%20visa%20Nhat%20Trung%20Han%20Anh%20Uc%20My%20-%20Kokono(1).jpg\" /></h3>\r\n\r\n<h2><strong>Kokono sẽ gi&uacute;p bạn l&agrave;m Dịch vụ l&agrave;m Visa Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ trọn g&oacute;i thế n&agrave;o?</strong></h2>\r\n\r\n<p>Qua 10 năm l&agrave;m hồ sơ VISA cho du học sinh tr&ecirc;n to&agrave;n quốc, tại Kokono, chữ &quot;T&iacute;n&quot; lu&ocirc;n được đặt l&ecirc;n h&agrave;ng đầu. Hơn ai hết, Kokono hiểu những chi ph&iacute; cơ hội lớn nhường n&agrave;o nếu bỏ lỡ c&aacute;c cơ hội kinh doanh, du lịch, l&agrave;m việc tại nước bạn chỉ v&igrave; VISA.&nbsp;Với ti&ecirc;u ch&iacute;:&nbsp;<em><strong>&quot;L&agrave;m VISA Nhanh ch&oacute;ng - Đơn giản - Đ&uacute;ng hạn&quot;</strong></em>, c&ugrave;ng đội ngũ chuy&ecirc;n vi&ecirc;n gi&agrave;u kinh nghiệm, am hiểu thủ tục v&agrave; quy tr&igrave;nh xin VISA, Kokono sẵn s&agrave;ng gi&uacute;p đỡ bạn xin VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; đ&uacute;ng hẹn nhất.<br />\r\n<br />\r\nĐể&nbsp;<em><strong>l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ...</strong></em>Kokono sẽ đại diện v&agrave; gi&uacute;p bạn trong to&agrave;n bộ qu&aacute; tr&igrave;nh ho&agrave;n thiện hồ sơ, thủ tục. R&agrave; so&aacute;t giấy tờ ch&iacute;nh x&aacute;c, logic theo từng loại VISA. Với đội ngũ chuy&ecirc;n vi&ecirc;n thủ tục d&agrave;y dặn kinh nghiệm, Kokono sẽ tiến h&agrave;nh dịch thuật v&agrave; c&ocirc;ng chứng c&aacute;c loại giấy tờ, ho&agrave;n thiện giấy tờ theo y&ecirc;u cầu của Đại sứ qu&aacute;n c&aacute;c nước. Đặt lịch hẹn, sắp xếp c&aacute;c loại giấy tờ theo thứ tự v&agrave; c&aacute;c c&ocirc;ng việc kh&aacute;c li&ecirc;n quan đến Đại sứ qu&aacute;n. C&aacute;c giai đoạn được tiến h&agrave;nh tỉ mỉ, đảm bảo bạn kh&ocirc;ng phải mất qu&aacute; nhiều thời gian v&agrave; y&ecirc;n t&acirc;m d&agrave;nh thời gian cho c&aacute;c c&ocirc;ng việc kh&aacute;c.</p>\r\n\r\n<p><br />\r\nB&ecirc;n cạnh đ&oacute;, Kokono sẽ giải th&iacute;ch to&agrave;n bộ quy tr&igrave;nh, th&ocirc;ng tin v&agrave; hồ sơ li&ecirc;n quan đến&nbsp;<strong>qu&aacute; tr&igrave;nh xin Visa đi Nhật, H&agrave;n, Trung, Anh, &Uacute;c, Mỹ&nbsp;</strong>để kh&aacute;ch h&agrave;ng hiểu tường tận v&agrave; nhận kết quả th&agrave;nh c&ocirc;ng nhất. B&ecirc;n cạnh đ&oacute;, đối với những kh&aacute;ch h&agrave;ng chưa từng đến&nbsp;<em>Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ</em>, Kokono sẽ c&oacute; những chia sẻ, lưu &yacute; về việc xuất nhập cảnh v&agrave; cuộc sống trong những ng&agrave;y lưu tr&uacute; tại nước bạn. Hy vọng những lời khuy&ecirc;n n&agrave;y sẽ gi&uacute;p bạn tự tin nhất để gặt h&aacute;i th&agrave;nh c&ocirc;ng cho chuyến đi tới những đất nước n&agrave;y!<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Xin visa Nhat Trung Han Anh Uc My - Kokono 2\" src=\"http://duhockokono.vn/pic/Service/images/Xin%20visa%20Nhat%20Trung%20Han%20Anh%20Uc%20My%20-%20Kokono%202.jpg\" /></p>\r\n\r\n<h3>Hồ sơ xin VISA đi Nhật Bản, Trung Quốc, H&agrave;n Quốc, Anh, &Uacute;c, Mỹ cần chuẩn bị những g&igrave;?</h3>\r\n\r\n<p>Để l&agrave;m hồ sơ xin VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ, bạn cần chuẩn bị c&aacute;c giấy tờ sau:&nbsp;<em>Hộ chiếu c&ograve;n hạn 6 th&aacute;ng, ảnh 4x6, X&aacute;c nhận c&ocirc;ng việc, bảng lương 3 th&aacute;ng gần nhất, số dư ng&acirc;n h&agrave;ng, bất động sản, thư mời (nếu c&oacute;).&nbsp;</em>&nbsp;Chi tiết Hồ sơ cần chuẩn bị, Kokono sẽ hướng dẫn chi tiết, cụ thể cho từng loại Visa!<br />\r\n<br />\r\n<strong><em>H&atilde;y y&ecirc;n t&acirc;m, bạn chỉ cần chụp ảnh hoặc scan c&aacute;c giấy tờ gửi trước, c&aacute;c thủ tục xin VISA rườm r&agrave; kh&aacute;c h&atilde;y để Kokono lo!</em></strong></p>\r\n\r\n<h3>Thời gian nhận v&agrave; chi ph&iacute; Dịch vụ l&agrave;m VISA Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ như thế n&agrave;o?</h3>\r\n\r\n<p>Thời gian nhận kết quả&nbsp;<em><strong>VISA&nbsp;</strong></em><em><strong>Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ&nbsp;</strong></em>khoảng từ&nbsp;5 - 15 ng&agrave;y l&agrave;m việc. (T&ugrave;y thuộc v&agrave;o loại VISA. V&iacute; dụ như Visa c&ocirc;ng t&aacute;c nhận được sau khoảng 5 - 7 ng&agrave;y l&agrave;m việc. Kokono sẽ trả lời thời gian ch&iacute;nh x&aacute;c thời gian nhận Visa cho từng trường hợp ngay khi tư vấn).<em>&nbsp;Hoặc lấy visa theo y&ecirc;u cầu (Vui l&ograve;ng li&ecirc;n hệ Hotline).</em><br />\r\n<br />\r\nGi&aacute; Dịch vụ:&nbsp;<strong>50$ -&nbsp;250$ /người (T&ugrave;y loại Visa)</strong><br />\r\n<br />\r\n<strong><em>&gt;&gt; Mọi thắc mắc về dịch vụ l&agrave;m Visa đi Nhật&nbsp;</em>(Visa du lịch, Visa c&ocirc;ng t&aacute;c, Visa du học, Visa tu nghiệp sinh)&nbsp;<em>vui l&ograve;ng li&ecirc;n hệ hotline&nbsp;</em></strong>để được tư vấn cụ thể nhất!<br />\r\n<br />\r\n<strong><img alt=\"Du học Nhật Bản năm 2018 - Kokono \" src=\"http://duhockokono.vn/pic/Service/images/Hotline%20Du%20hoc%20Kokono.png\" /></strong><strong>___</strong><strong>Hotline:&nbsp;</strong><em><strong>0912.916.322</strong></em><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Sự th&agrave;nh c&ocirc;ng của bạn ch&iacute;nh l&agrave; động lực của ch&uacute;ng t&ocirc;i!</strong></p>\r\n\r\n<p><br />\r\n<strong>Kokono lu&ocirc;n sẵn s&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n chặng đường l&agrave;m VISA đi Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ. Li&ecirc;n hệ với Kokono qua Hotline ngay để ho&agrave;n thiện hồ sơ l&agrave;m Visa sớm nhất!&nbsp;</strong></p>\r\n', ''),
(4, 2, 'DỊCH VỤ CHO THUÊ PHIÊN DỊCH TIẾNG NHẬT, TRUNG, HÀN', '<p><strong><em>Dịch vụ cho thu&ecirc; phi&ecirc;n dịch - Tiếng Nhật tại H&agrave; Nội</em></strong><strong>&nbsp;đang được thực hiện bởi C&ocirc;ng Ty Cổ Phần Phi&ecirc;n Dịch Quốc Tế Kokono. Tự h&agrave;o l&agrave; c&ocirc;ng ty chuy&ecirc;n cung cấp dịch vụ Phi&ecirc;n dịch đ&atilde; c&oacute; tr&ecirc;n 10 năm kinh nghiệm trong lĩnh vực Tiếng Nhật. C&ugrave;ng đội ngũ phi&ecirc;n dịch vi&ecirc;n ki&ecirc;m giảng vi&ecirc;n chất lượng, đ&atilde; từng sinh sống v&agrave; l&agrave;m việc tại Nhật Bản tr&ecirc;n 3 năm, đ&atilde; c&oacute; kinh nghiệm, phong th&aacute;i v&agrave; lối phi&ecirc;n dịch như người bản xứ. Đến với Kokono để hưởng dịch vụ tốt nhất v&agrave; chi ph&iacute; rẻ nhất!</strong><br />\r\n<br />\r\n<strong><img alt=\"Du học Nhật Bản năm 2018 - Kokono \" src=\"http://duhockokono.vn/pic/Service/images/Hotline%20Du%20hoc%20Kokono.png\" style=\"height:30px; width:30px\" /></strong><strong>___&nbsp;</strong><strong>Gọi ngay Hotline:&nbsp;</strong><em><strong>0912.916.322</strong></em></p>\r\n\r\n<h3>KOKONO H&Agrave; NỘI - CUNG CẤP DỊCH VỤ PHI&Ecirc;N DỊCH TIẾNG NHẬT TẠI H&Agrave; NỘI CHẤT LƯỢNG CAO&nbsp;</h3>\r\n\r\n<p>Ng&ocirc;n ngữ đ&atilde; trở th&agrave;nh cầu nối tối quan trọng của c&aacute;c mối quan hệ, đặc biệt l&agrave; quốc tế.&nbsp;Trước h&ecirc;́t, quá trình giao ti&ecirc;́p của con người chỉ có th&ecirc;̉ được thực hi&ecirc;̣n tr&ecirc;n ti&ecirc;̀n đ&ecirc;̀ m&ocirc;̣t loại ng&ocirc;n ngữ đ&ocirc;̀ng nh&acirc;́t. Đ&ocirc;́i với những người sử dụng các ng&ocirc;n ngữ khác nhau, hoạt đ&ocirc;̣ng giao ti&ecirc;́p bắt bu&ocirc;̣c phải th&ocirc;ng qua phi&ecirc;n dịch. Do đó, phi&ecirc;n dịch đóng vai trò r&acirc;́t quan trọng trong vi&ecirc;̣c k&ecirc;́t n&ocirc;́i con người ở những n&ecirc;̀n văn hoá khác nhau.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/AAEAAQAAAAAAAARuAAAAJDlmYzI5NGI2LWM0NDQtNDc4Zi1iOTI2LTZiNGJhOTM4NDJlOA.jpg\" style=\"height:262px; width:457px\" /></strong></p>\r\n\r\n<p><br />\r\nHiện nay Nhật bản đang đầu tư rất mạnh v&agrave;o Việt Nam. C&aacute;c khu c&ocirc;ng nghiệp lớn, c&aacute;c &ldquo;&ocirc;ng tr&ugrave;m&rdquo; trong lĩnh vực c&ocirc;ng nghiệp đều coi Việt Nam l&agrave; thị trường chiến lược trong khu vực Đ&ocirc;ng Nam &Aacute;, kh&ocirc;ng chỉ đầu tư cơ sở vật chất, trang thiết bị, m&agrave; c&ograve;n ch&uacute; trọng ph&aacute;t triển nh&acirc;n tố con người. Bởi vậy cần đến c&aacute;c chuy&ecirc;n gia phi&ecirc;n dịch tiếng Nhật để l&agrave;m cầu nối trong kh&acirc;u chuyển giao c&ocirc;ng nghệ, l&agrave;m chủ c&ocirc;ng nghệ từ nh&acirc;n sự bản địa.</p>\r\n\r\n<p><br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/Dich-vu-phien-dich-tieng-nhat-trung-han-Kokono.gif\" style=\"height:137px; width:756px\" /></p>\r\n\r\n<h3><strong>Kokono H&agrave; Nội đ&atilde; c&oacute; kinh nghiệm l&agrave;m việc v&agrave; l&agrave; đối t&aacute;c với người Nhật l&acirc;u năm, v&agrave; ch&uacute;ng t&ocirc;i hiểu chữ &ldquo;T&iacute;n&rdquo; l&agrave; t&ocirc;n chỉ m&agrave; bất cứ một đối t&aacute;c Nhật n&agrave;o cũng cần ở người Việt. Do vậy, Kokono H&agrave; Nội cam kết đem lại cho kh&aacute;ch h&agrave;ng&nbsp;<em>Dịch vụ cho thu&ecirc; phi&ecirc;n dịch Tiếng Nhật</em>:</strong></h3>\r\n\r\n<p><img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/chitay.png\" style=\"height:25px; width:25px\" /><strong>&nbsp;Chi nh&aacute;nh rộng khắp&nbsp;</strong>đến 63 tỉnh th&agrave;nh tr&ecirc;n cả nước. C&oacute; đến 10 chi nh&aacute;nh tại H&agrave; Nội để thuận tiện cho từng khu vực của bạn.<br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/chitay.png\" style=\"height:25px; width:25px\" />&nbsp;Phi&ecirc;n dịch vi&ecirc;n&nbsp;<strong>CHUY&Ecirc;N NGHIỆP</strong>, đầu v&agrave;o được tuyển chọn gắt gao. Tất cả đều l&agrave; người đ&atilde; từng sinh sống v&agrave; l&agrave;m việc tại Nhật Bản tr&ecirc;n 3 năm, am hiểu kiến thức chuy&ecirc;n ng&agrave;nh, d&agrave;y dặn kinh nghiệm v&agrave; lối văn như người bản xứ.<br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/chitay.png\" style=\"height:25px; width:25px\" />&nbsp;Chi ph&iacute; hợp l&yacute;, cam kết&nbsp;<strong>RẺ NHẤT THỊ TRƯỜNG</strong>.<br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/chitay.png\" style=\"height:25px; width:25px\" />Chất lượng được đảm bảo theo y&ecirc;u cầu của từng dự &aacute;n cho th&agrave;nh c&ocirc;ng của bạn v&agrave; doanh nghiệp.<br />\r\n<br />\r\n<br />\r\n<em>Kokono nhận cung cấp&nbsp;<strong>dịch vụ cho thu&ecirc; Phi&ecirc;n dịch vi&ecirc;n Tiếng Nhật tại H&agrave; Nội</strong>&nbsp;24/7 ở T<strong>ẤT CẢ C&Aacute;C LĨNH VỰC!&nbsp;</strong></em><br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/arrow.gif\" style=\"height:21px; width:40px\" />Gọi ngay Hotline:&nbsp;<em><strong>0912.916.322</strong></em><strong>&nbsp;</strong>để t&igrave;m phi&ecirc;n dịch vi&ecirc;n Tiếng Nhật ph&ugrave; hợp nhất cho bạn v&agrave; doanh nghiệp!</p>\r\n\r\n<p><img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/ha%20noi.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<h2><br />\r\nLộ tr&igrave;nh&nbsp;<em>Dịch vụ cho thu&ecirc; Phi&ecirc;n dịch Tiếng Nhật tại Kokono H&agrave; Nội</em>:&nbsp;</h2>\r\n\r\n<p><img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/9T4Lqkybc.png\" style=\"height:23px; width:25px\" />&nbsp;Kh&aacute;ch h&agrave;ng li&ecirc;n hệ qua&nbsp;<em><strong>Hotline:&nbsp;</strong></em><em><strong>0912.916.322</strong></em>&nbsp;để nhận tư vấn;<br />\r\n<br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/9T4Lqkybc.png\" style=\"height:23px; width:25px\" />&nbsp;Kokono c&ugrave;ng kh&aacute;ch h&agrave;ng thống nhất c&aacute;c điều khoản về dịch vụ v&agrave; lựa chọn phi&ecirc;n dịch vi&ecirc;n ph&ugrave; hợp;<br />\r\n<br />\r\n<img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/9T4Lqkybc.png\" style=\"height:23px; width:25px\" />&nbsp;Sau khi hai b&ecirc;n thống nhất, kh&aacute;ch h&agrave;ng v&agrave; Kokono tiến h&agrave;nh k&yacute; hợp đồng v&agrave; thực hiện c&aacute;c nghĩa vụ theo hợp đồng.<br />\r\n<br />\r\n<strong><em>(*) Gi&aacute; dịch vụ phi&ecirc;n dịch Tiếng Nhật tại Kokono H&agrave; Nội cam kết RẺ NHẤT THỊ TRƯỜNG v&agrave; t&ugrave;y v&agrave;o t&iacute;nh chất v&agrave; mức độ y&ecirc;u cầu của c&ocirc;ng việc.&nbsp;</em></strong></p>\r\n\r\n<h3><strong>Đến ngay C&ocirc;ng ty cổ phần phi&ecirc;n dịch Quốc tế Kokono&nbsp;tại 10 chi nh&aacute;nh ở H&agrave; Nội:</strong></h3>\r\n\r\n<p>Kokono c&oacute; đến 10 chi nh&aacute;nh tại H&agrave; Nội để thuận tiện nhất cho điều kiện của kh&aacute;ch h&agrave;ng. Đến ngay cơ sở gần bạn nhất để chọn&nbsp;<strong>dịch vụ Phi&ecirc;n dịch Tiếng Nhật</strong>&nbsp;tốt nhất, hoặc thuận tiện hơn h&atilde;y gọi ngay Hotline:&nbsp;<em><strong>0912.916.322</strong></em><strong>&nbsp;</strong>ch&uacute;ng t&ocirc;i sẽ hỗ trợ bạn ngay lập tức!&nbsp;<br />\r\n<br />\r\n<strong>TRỤ SỞ CH&Iacute;NH:&nbsp;<em>Số 04 - Ng&otilde; 322 L&ecirc; Trọng Tấn -</em><em>&nbsp;Thanh Xu&acirc;n&nbsp;-&nbsp;H&agrave; Nội</em></strong><br />\r\n<br />\r\nCơ sở&nbsp;2: Đường Li&ecirc;n Cơ (đầu đường Nguyễn Cơ Thạch rẽ phải) -&nbsp;<strong>Cầu Giấy</strong>&nbsp;-&nbsp;<strong>H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở&nbsp;3: Đường&nbsp;<strong>Nguyễn Ch&iacute; Thanh -&nbsp;</strong><strong>Đống Đa</strong>&nbsp;-&nbsp;<strong>H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở&nbsp;4: Đường<strong>&nbsp;Minh Khai -&nbsp;</strong><strong>Hai B&agrave; Trưng</strong>&nbsp;<strong>- H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở&nbsp;5:&nbsp;<strong>Khu Đ&ocirc; Thị</strong><strong>&nbsp;Việt Hưng -&nbsp;</strong><strong>Long Bi&ecirc;n</strong>&nbsp;-<strong>&nbsp;H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở&nbsp;6: Đường&nbsp;<strong>Quang Trung</strong>&nbsp;-&nbsp;<strong>H&agrave; Đ&ocirc;ng</strong>&nbsp;-&nbsp;<strong>H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở&nbsp;7: Đường&nbsp;<em>Thanh Liệt (Gần UBND x&atilde; Thanh Liệt, gần Ch&ugrave;a Bằng)</em>&nbsp;-&nbsp;&nbsp;<strong>Thanh Tr&igrave;</strong>&nbsp;-&nbsp;<strong>H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở&nbsp;8: Chợ T&oacute; - Uy Nỗ - Thị Trấn&nbsp;<strong>Đ&ocirc;ng Anh</strong><strong>&nbsp;</strong>-<strong>&nbsp;H&agrave; Nội</strong><br />\r\n<br />\r\nCơ sở 9:&nbsp;Đường<strong>&nbsp;Cổ Bi &ndash;&nbsp;Gia L&acirc;m&nbsp;</strong>&ndash; H&agrave; Nội.<br />\r\n<br />\r\nCơ sở&nbsp;10: Đường&nbsp;Đinh Ti&ecirc;n Ho&agrave;ng &ndash; Thị x&atilde; &nbsp;<strong>Sơn T&acirc;y</strong><strong>&nbsp;</strong>-&nbsp;<strong>H&agrave; Nội</strong><br />\r\n<br />\r\n<strong><img alt=\"Dịch vụ cho thuê phiên dịch - Tiếng Nhật tại Hà Nội\" src=\"http://duhockokono.vn/pic/Service/images/Hotline%20Du%20hoc%20Kokono.png\" style=\"height:30px; width:30px\" />&nbsp;</strong>Mọi thắc mắc xin li&ecirc;n hệ tới&nbsp;<strong>HOTLINE:</strong>&nbsp;<em><strong>0912.916.322</strong></em><strong>&nbsp;</strong>để nhận tư vấn đầy đủ nhất v&agrave; hưởng c&aacute;c ưu đ&atilde;i sớm nhất về&nbsp;<strong><em>Dịch vụ cho thu&ecirc; Phi&ecirc;n dịch Tiếng Nhật tại H&agrave; Nội</em></strong>.</p>\r\n\r\n<p><strong>Kokono H&agrave; Nội h&acirc;n hạnh được đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n con đường tới th&agrave;nh c&ocirc;ng!</strong></p>\r\n', ''),
(8, 2, 'HƯỚNG DẪN HỒ SƠ DU HỌC NHẬT BẢN - HƯỚNG DẪN CHI TIẾT', '<p><strong>Nhật Bản l&agrave; điểm đến kh&ocirc;ng c&ograve;n xa lạ đối với học sinh Việt Nam chọn lựa con đường du học cho m&igrave;nh. Với hồ sơ du học đơn giản; học ph&iacute;, chi ph&iacute; thấp c&ugrave;ng việc được ph&eacute;p l&agrave;m th&ecirc;m trong thời gian học, Nhật Bản đ&atilde; trở th&agrave;nh&nbsp;</strong><strong>điểm đến hơn 300.000 du học sinh tr&ecirc;n to&agrave;n thế giới.</strong><strong>&nbsp;Vậy, Hồ sơ du học Nhật cần chuẩn bị những g&igrave;? Quy tr&igrave;nh du học Nhật Bản như thế n&agrave;o? Kokono xin chia sẻ một số th&ocirc;ng tin sau.</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><a href=\"http://duhockokono.vn/tuyen-sinh-du-hoc-tai-nhat-ban-ky-thang-10-nam-2017.htm\" target=\"_blank\"><img alt=\"Du hoc Nhat Ban, Du học Nhật Bản\" src=\"http://duhockokono.vn/pic/New/images/Tim%20thong%20tin%20Du%20hoc%20Nhat%20Ban(7).png\" /></a></p>\r\n\r\n<p><br />\r\n<strong><a href=\"http://duhockokono.vn/quy-dinh-moi-khi-nhap-canh-vao-nhat-ban.htm\">&gt;&gt; Quy định mới khi nhập cảnh v&agrave;o Nhật Bản</a><br />\r\n<a href=\"http://duhockokono.vn/nguoi-bao-lanh-du-hoc-nhat-ban.htm\">&gt;&gt; Người bảo l&atilde;nh đối với Du học sinh khi đi du học Nhật Bản</a></strong><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Du học Kokono&nbsp;</strong>xin hướng dẫn<em><strong>&nbsp;<a href=\"http://duhockokono.vn/ho-so-va-quy-trinh-du-hoc-nhat.htm\">Hồ sơ Du học Nhật Bản</a>&nbsp;</strong></em>như sau:</p>\r\n\r\n<h2><strong>1. Điều kiện nộp Hồ sơ Du học Nhật Bản</strong></h2>\r\n\r\n<p>- Học sinh đ&atilde; tốt nghiệp PTTH trở l&ecirc;n</p>\r\n\r\n<p>- C&oacute; tr&igrave;nh độ tiếng Nhật sơ cấp</p>\r\n\r\n<p>- C&oacute; đủ điều kiện t&agrave;i ch&iacute;nh để học tập<br />\r\n<br />\r\nDo Ch&iacute;nh s&aacute;ch th&uacute;c đẩy gi&aacute;o dục, Ch&iacute;nh phủ Nhật Bản đ&atilde; c&oacute; rất nhiều ch&iacute;nh s&aacute;ch mở cửa đối với du học sinh Việt Nam. Bởi vậy,<strong>&nbsp;hồ sơ v&agrave; thủ tục du học Nhật Bản</strong>&nbsp;cũng đơn giản hơn nhiều so với du học c&aacute;c nước kh&aacute;c. Li&ecirc;n hệ Kokono ngay để&nbsp;<a href=\"http://duhockokono.vn/tuyen-sinh-du-hoc-tai-nhat-ban-ky-thang-10-nam-2017.htm\" target=\"_blank\">kịp thời xử l&yacute; hồ sơ cho đợt Tuyển sinh du học Nhật Bản sắp tới</a>.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Du học Nhật Bản Kokono Hướng dẫn hồ sơ du học 1.jpg\" src=\"http://duhockokono.vn/pic/Service/images/Du%20h%E1%BB%8Dc%20Nh%E1%BA%ADt%20B%E1%BA%A3n%20Kokono%20H%C6%B0%E1%BB%9Bng%20d%E1%BA%ABn%20h%E1%BB%93%20s%C6%A1%20du%20h%E1%BB%8Dc%201.jpg\" /></p>\r\n\r\n<h2><strong>2. Hồ sơ Du học Nhật Bản cần chuẩn bị</strong></h2>\r\n\r\n<p>- &nbsp;Bằng tốt nghiệp PTTH, học bạ PTTH (bản gốc)</p>\r\n\r\n<p>- &nbsp;Bằng tốt nghiệp, bảng điểm của cấp học cao nhất (bản gốc)&nbsp;</p>\r\n\r\n<p>- &nbsp;Sơ yếu l&yacute; lịch c&oacute; x&aacute;c nhận của ch&iacute;nh quyền địa phương (bản gốc)&nbsp;</p>\r\n\r\n<p>- &nbsp;Chứng minh thư nh&acirc;n d&acirc;n của học sinh v&agrave; của cha mẹ (bản gốc)&nbsp;</p>\r\n\r\n<p>- &nbsp;Sổ hộ khẩu gia đ&igrave;nh (bản gốc)&nbsp;</p>\r\n\r\n<p>- &nbsp;Giấy khai sinh 03 bản sao (mới nhất)&nbsp;</p>\r\n\r\n<p>- &nbsp;Giấy kh&aacute;m sức khỏe&nbsp;</p>\r\n\r\n<p>- &nbsp;Hộ chiếu (Passport)&nbsp;</p>\r\n\r\n<p>- &nbsp;20 ảnh 3x4 (ph&ocirc;ng trắng)&nbsp;</p>\r\n\r\n<p>- &nbsp;10 ảnh 4x6 (ph&ocirc;ng trắng)</p>\r\n\r\n<p><br />\r\nMỗi trường hợp&nbsp;<strong>hồ sơ du học Nhật Bản</strong>&nbsp;sẽ c&oacute; những hướng xử l&yacute; kh&aacute;c nhau, Kokono sẽ tư vấn cụ thể dựa tr&ecirc;n mong muốn của bạn.<br />\r\n<strong><em>Chỉ cần bạn mong muốn du học tại Nhật Bản, mọi thủ tục h&atilde;y để Kokono lo!</em></strong></p>\r\n', ''),
(9, 2, 'DỊCH VỤ LÀM VISA NHẬT, HÀN, TRUNG, ANH, ÚC, MỸ TRỌN GÓI - SIÊU NHANH, SIÊU RẺ', '<p><strong>Dịch vụ l&agrave;m Visa trọn g&oacute;i<em>&nbsp;(Visa Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ)&nbsp;</em>được cung cấp bởi C&ocirc;ng ty Cổ phần Tư vấn Du học Kokono. Qua 10 năm ho&agrave;n thiện Visa cho du học sinh tr&ecirc;n cả nước, Kokono mong muốn d&agrave;nh những kinh nghiệm qu&yacute; b&aacute;u đ&atilde; t&iacute;ch lũy được trong suốt thời gian qua gi&uacute;p bạn lấy được&nbsp;<em>Visa du lịch, Visa c&ocirc;ng t&aacute;c, Visa du học, Visa Tu nghiệp sinh (Visa xuất khẩu lao động)</em>&nbsp;một c&aacute;ch nhanh ch&oacute;ng v&agrave; thuận tiện nhất. Đến với Kokono, bạn h&atilde;y d&agrave;nh thời gian cho việc chuẩn bị h&agrave;nh tr&igrave;nh sắp tới, việc lấy Visa Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ h&atilde;y để ch&uacute;ng t&ocirc;i lo!</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Xin visa Nhat Trung Han Anh Uc My - Kokono 3\" src=\"http://duhockokono.vn/pic/Service/images/Xin%20visa%20Nhat%20Trung%20Han%20Anh%20Uc%20My%20-%20Kokono%203(1).jpg\" /></p>\r\n\r\n<p><br />\r\nViệc tự&nbsp;<em><strong>l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ&nbsp;</strong></em>m&agrave; kh&ocirc;ng sử dụng&nbsp;<strong>dịch vụ l&agrave;m VISA&nbsp;</strong>sẽ v&ocirc; c&ugrave;ng kh&oacute; khăn v&agrave; nguy cơ bị trượt rất cao. Đặc biệt trong trường hợp, bạn kh&ocirc;ng c&oacute; thời gian, tr&igrave;nh độ tiếng Anh kh&ocirc;ng ổn, kh&ocirc;ng biết tiếng v&agrave; kh&ocirc;ng biết thủ tục l&agrave;m Visa tới đất nước đ&oacute;. Mọi thứ trở n&ecirc;n ngo&agrave;i tầm với. B&ecirc;n cạnh đ&oacute;, c&aacute;c điều kiện xin Visa từ việc chuẩn bị hồ sơ, chứng minh t&agrave;i ch&iacute;nh, bảo l&atilde;nh&hellip;đều y&ecirc;u cầu khắt khe v&agrave; khiến bạn mệt mỏi v&agrave; &quot;stress&quot;.&nbsp;<em>Bởi vậy, h&atilde;y giảm thiểu rủi ro bằng c&aacute;ch lựa chọn một đơn vị uy t&iacute;n v&agrave; d&agrave;y dặn kinh nghiệm để chuyến đi được tiết kiệm, an to&agrave;n v&agrave; thoải m&aacute;i nhất.&nbsp;</em><strong>Để l&agrave;m Dịch vụ VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ trọn g&oacute;i</strong>&nbsp;vui l&ograve;ng gọi hotline:&nbsp;<em><strong>0912.916.322&nbsp;</strong></em>để ho&agrave;n thiện hồ sơ một c&aacute;ch dễ d&agrave;ng, nhanh ch&oacute;ng v&agrave; thuận lợi nhất!</p>\r\n\r\n<h3>Bạn c&oacute; thể tự l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ kh&ocirc;ng?</h3>\r\n\r\n<p>C&acirc;u trả lời l&agrave; c&oacute;. Bạn ho&agrave;n to&agrave;n c&oacute; thể tự&nbsp;<em><strong>l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ</strong></em>. Tuy nhi&ecirc;n, nếu kh&ocirc;ng nắm r&otilde; thu tục hồ sơ xin VISA, bạn sẽ tốn rất nhiều c&ocirc;ng sức, thời gian, tiền bạc m&agrave; đ&ocirc;i khi kh&ocirc;ng đạt được kết quả. Nếu bị từ chối VISA, bạn vừa mất cơ hội du lịch hoặc l&agrave;m việc, vừa g&acirc;y ra những l&atilde;ng ph&iacute; v&agrave; tốn k&eacute;m kh&ocirc;ng cần thiết. Bởi vậy, để kh&ocirc;ng phải lo lắng về quy tr&igrave;nh, thủ tục, kh&ocirc;ng cần mất qu&aacute; nhiều thời gian nghi&ecirc;n cứu, kh&ocirc;ng lo hồ sơ bị trả về...h&atilde;y để Kokono gi&uacute;p bạn!<br />\r\n<br />\r\n<strong>&gt;&gt; Đăng k&yacute; tư vấn l&agrave;m Visa miễn ph&iacute; qua hotline:</strong><br />\r\n<br />\r\n<strong><img alt=\"Du học Nhật Bản năm 2018 - Kokono \" src=\"http://duhockokono.vn/pic/Service/images/Hotline%20Du%20hoc%20Kokono.png\" /></strong><strong>___</strong><strong>Hotline:&nbsp;</strong><em><strong>0912.916.322</strong></em></p>\r\n\r\n<h3><img alt=\"Xin visa Nhat Trung Han Anh Uc My - Kokono\" src=\"http://duhockokono.vn/pic/Service/images/Xin%20visa%20Nhat%20Trung%20Han%20Anh%20Uc%20My%20-%20Kokono(1).jpg\" /></h3>\r\n\r\n<h2><strong>Kokono sẽ gi&uacute;p bạn l&agrave;m Dịch vụ l&agrave;m Visa Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ trọn g&oacute;i thế n&agrave;o?</strong></h2>\r\n\r\n<p>Qua 10 năm l&agrave;m hồ sơ VISA cho du học sinh tr&ecirc;n to&agrave;n quốc, tại Kokono, chữ &quot;T&iacute;n&quot; lu&ocirc;n được đặt l&ecirc;n h&agrave;ng đầu. Hơn ai hết, Kokono hiểu những chi ph&iacute; cơ hội lớn nhường n&agrave;o nếu bỏ lỡ c&aacute;c cơ hội kinh doanh, du lịch, l&agrave;m việc tại nước bạn chỉ v&igrave; VISA.&nbsp;Với ti&ecirc;u ch&iacute;:&nbsp;<em><strong>&quot;L&agrave;m VISA Nhanh ch&oacute;ng - Đơn giản - Đ&uacute;ng hạn&quot;</strong></em>, c&ugrave;ng đội ngũ chuy&ecirc;n vi&ecirc;n gi&agrave;u kinh nghiệm, am hiểu thủ tục v&agrave; quy tr&igrave;nh xin VISA, Kokono sẵn s&agrave;ng gi&uacute;p đỡ bạn xin VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; đ&uacute;ng hẹn nhất.<br />\r\n<br />\r\nĐể&nbsp;<em><strong>l&agrave;m VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ...</strong></em>Kokono sẽ đại diện v&agrave; gi&uacute;p bạn trong to&agrave;n bộ qu&aacute; tr&igrave;nh ho&agrave;n thiện hồ sơ, thủ tục. R&agrave; so&aacute;t giấy tờ ch&iacute;nh x&aacute;c, logic theo từng loại VISA. Với đội ngũ chuy&ecirc;n vi&ecirc;n thủ tục d&agrave;y dặn kinh nghiệm, Kokono sẽ tiến h&agrave;nh dịch thuật v&agrave; c&ocirc;ng chứng c&aacute;c loại giấy tờ, ho&agrave;n thiện giấy tờ theo y&ecirc;u cầu của Đại sứ qu&aacute;n c&aacute;c nước. Đặt lịch hẹn, sắp xếp c&aacute;c loại giấy tờ theo thứ tự v&agrave; c&aacute;c c&ocirc;ng việc kh&aacute;c li&ecirc;n quan đến Đại sứ qu&aacute;n. C&aacute;c giai đoạn được tiến h&agrave;nh tỉ mỉ, đảm bảo bạn kh&ocirc;ng phải mất qu&aacute; nhiều thời gian v&agrave; y&ecirc;n t&acirc;m d&agrave;nh thời gian cho c&aacute;c c&ocirc;ng việc kh&aacute;c.</p>\r\n\r\n<p><br />\r\nB&ecirc;n cạnh đ&oacute;, Kokono sẽ giải th&iacute;ch to&agrave;n bộ quy tr&igrave;nh, th&ocirc;ng tin v&agrave; hồ sơ li&ecirc;n quan đến&nbsp;<strong>qu&aacute; tr&igrave;nh xin Visa đi Nhật, H&agrave;n, Trung, Anh, &Uacute;c, Mỹ&nbsp;</strong>để kh&aacute;ch h&agrave;ng hiểu tường tận v&agrave; nhận kết quả th&agrave;nh c&ocirc;ng nhất. B&ecirc;n cạnh đ&oacute;, đối với những kh&aacute;ch h&agrave;ng chưa từng đến&nbsp;<em>Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ</em>, Kokono sẽ c&oacute; những chia sẻ, lưu &yacute; về việc xuất nhập cảnh v&agrave; cuộc sống trong những ng&agrave;y lưu tr&uacute; tại nước bạn. Hy vọng những lời khuy&ecirc;n n&agrave;y sẽ gi&uacute;p bạn tự tin nhất để gặt h&aacute;i th&agrave;nh c&ocirc;ng cho chuyến đi tới những đất nước n&agrave;y!<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Xin visa Nhat Trung Han Anh Uc My - Kokono 2\" src=\"http://duhockokono.vn/pic/Service/images/Xin%20visa%20Nhat%20Trung%20Han%20Anh%20Uc%20My%20-%20Kokono%202.jpg\" /></p>\r\n\r\n<h3>Hồ sơ xin VISA đi Nhật Bản, Trung Quốc, H&agrave;n Quốc, Anh, &Uacute;c, Mỹ cần chuẩn bị những g&igrave;?</h3>\r\n\r\n<p>Để l&agrave;m hồ sơ xin VISA đi Nhật, Trung, H&agrave;n, Anh, &Uacute;c, Mỹ, bạn cần chuẩn bị c&aacute;c giấy tờ sau:&nbsp;<em>Hộ chiếu c&ograve;n hạn 6 th&aacute;ng, ảnh 4x6, X&aacute;c nhận c&ocirc;ng việc, bảng lương 3 th&aacute;ng gần nhất, số dư ng&acirc;n h&agrave;ng, bất động sản, thư mời (nếu c&oacute;).&nbsp;</em>&nbsp;Chi tiết Hồ sơ cần chuẩn bị, Kokono sẽ hướng dẫn chi tiết, cụ thể cho từng loại Visa!<br />\r\n<br />\r\n<strong><em>H&atilde;y y&ecirc;n t&acirc;m, bạn chỉ cần chụp ảnh hoặc scan c&aacute;c giấy tờ gửi trước, c&aacute;c thủ tục xin VISA rườm r&agrave; kh&aacute;c h&atilde;y để Kokono lo!</em></strong></p>\r\n\r\n<h3>Thời gian nhận v&agrave; chi ph&iacute; Dịch vụ l&agrave;m VISA Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ như thế n&agrave;o?</h3>\r\n\r\n<p>Thời gian nhận kết quả&nbsp;<em><strong>VISA&nbsp;</strong></em><em><strong>Nhật Bản, H&agrave;n Quốc, Trung Quốc, Anh, &Uacute;c, Mỹ&nbsp;</strong></em>khoảng từ&nbsp;5 - 15 ng&agrave;y l&agrave;m việc. (T&ugrave;y thuộc v&agrave;o loại VISA. V&iacute; dụ như Visa c&ocirc;ng t&aacute;c nhận được sau khoảng 5 - 7 ng&agrave;y l&agrave;m việc. Kokono sẽ trả lời thời gian ch&iacute;nh x&aacute;c thời gian nhận Visa cho từng trường hợp ngay khi tư vấn).<em>&nbsp;Hoặc lấy visa theo y&ecirc;u cầu (Vui l&ograve;ng li&ecirc;n hệ Hotline).</em><br />\r\n<br />\r\nGi&aacute; Dịch vụ:&nbsp;<strong>50$ -&nbsp;250$ /người (T&ugrave;y loại Visa)</strong><br />\r\n<br />\r\n<strong><em>&gt;&gt; Mọi thắc mắc về dịch vụ l&agrave;m Visa đi Nhật&nbsp;</em>(Visa du lịch, Visa c&ocirc;ng t&aacute;c, Visa du học, Visa tu nghiệp sinh)&nbsp;<em>vui l&ograve;ng li&ecirc;n hệ hotline&nbsp;</em></strong>để được tư vấn cụ thể nhất!<br />\r\n<br />\r\n<strong><img alt=\"Du học Nhật Bản năm 2018 - Kokono \" src=\"http://duhockokono.vn/pic/Service/images/Hotline%20Du%20hoc%20Kokono.png\" /></strong><strong>___</strong><strong>Hotline:&nbsp;</strong><em><strong>0912.916.322</strong></em><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Sự th&agrave;nh c&ocirc;ng của bạn ch&iacute;nh l&agrave; động lực của ch&uacute;ng t&ocirc;i!</strong></p>\r\n', '');
INSERT INTO `post` (`id`, `cate_id`, `title`, `content`, `description`) VALUES
(10, 3, 'NHẬT BẢN TĂNG CƯỜNG TUYỂN DỤNG TU NGHIỆP SINH VIỆT NAM', '<p>&Ocirc;ng L&ecirc; Văn Thanh, Ph&oacute; Cục trưởng Quản l&yacute; lao động ngo&agrave;i nước (Bộ LĐ-TB&amp;XH), cho biết, sau thảm họa động đất, s&oacute;ng thần, Nhật Bản đ&aacute;nh gi&aacute; cao tu nghiệp sinh (TNS) Việt Nam v&igrave; tinh thần l&agrave;m việc v&agrave; những h&agrave;nh động chia sẻ mất m&aacute;t với người d&acirc;n Nhật. Nước n&agrave;y sẽ tăng cường tiếp nhận tu nghiệp sinh Việt Nam trong thời gian tới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://duhockokono.vn/pic/Service/images/636120537684700366.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu nghiệp sinh Việt Nam tại x&iacute; nghiệp tỉnh Ishikawa Nhật Bản</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; Coi trọng tu nghiệp sinh Việt Nam</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &Ocirc;ng L&ecirc; Văn Thanh cho biết, sau khi xảy ra thảm họa động đất s&oacute;ng thần, tu nghiệp sinh Việt Nam l&agrave;m việc tại Nhật Bản vẫn kh&ocirc;ng hoang mang. Đa số tu nghiệp sinh Việt Nam tham gia l&agrave;m việc b&igrave;nh thường v&agrave; đăng k&yacute; l&agrave;m th&ecirc;m giờ kh&ocirc;ng nhận lương để g&oacute;p phần xoa dịu nỗi đau với người d&acirc;n Nhật Bản. T&igrave;nh cảm đ&oacute; của TNS Việt Nam đ&atilde; được Ch&iacute;nh phủ Nhật Bản đ&aacute;nh gi&aacute; rất cao v&agrave; mong muốn gia tăng tiếp nhận TNS Việt Nam trong năm t&agrave;i kh&oacute;a 2011.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; Ban Quản l&yacute; lao động Việt Nam tại Nhật Bản cũng cho biết, sau thảm họa động đất s&oacute;ng thần, h&igrave;nh ảnh tu nghiệp sinh Việt Nam đang g&acirc;y ấn tượng hơn so với TNS một số nước kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp;Theo &ocirc;ng Thanh, hiện c&oacute; khoảng 18.000 TNS Việt Nam đang l&agrave;m việc tại Nhật Bản. Trước đ&acirc;y, khi lao động Việt Nam sang tu nghiệp tại Nhật, năm thứ nhất họ chỉ được hưởng trợ cấp tu nghiệp, đến năm thứ hai v&agrave; thứ ba mới được hưởng lương n&ecirc;n thu nhập kh&ocirc;ng cao. &ldquo;Hiện, theo quy định mới của Nhật Bản, ngay từ năm thứ nhất, tu nghiệp sinh được coi l&agrave; thực tập sinh v&agrave; được nhận lương như lao động bản địa n&ecirc;n thu nhập cao hơn&rdquo; &ndash; &ocirc;ng Thanh n&oacute;i. Theo &ocirc;ng Thanh, trung b&igrave;nh, một TNS, sau khi đ&atilde; trừ c&aacute;c chi ph&iacute;, mỗi th&aacute;ng nhận được khoảng 80.000 y&ecirc;n (tương đương với khoảng 20 triệu đồng).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; Đại diện Ph&ograve;ng Thị trường (Cục Quản l&yacute; lao động ngo&agrave;i nước) cũng cho biết, trong 4 th&aacute;ng đầu năm, c&oacute; nhiều nghiệp đo&agrave;n của Nhật Bản sang Việt Nam trực tiếp tuyển lao động. Ch&iacute;nh v&igrave; thế, ri&ecirc;ng trong th&aacute;ng 4, đ&atilde; c&oacute; gần 600 lao động được đưa sang Nhật Bản tu nghiệp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>&nbsp; &nbsp; &nbsp;Đơn h&agrave;ng nhiều, lương cao</strong></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &Ocirc;ng Nguyễn Xu&acirc;n Vui &ndash; Chủ tịch HĐQT ki&ecirc;m Tổng Gi&aacute;m đốc Cty Cổ phần dịch vụ v&agrave; thương mại H&agrave;ng kh&ocirc;ng (Airseco) cho biết, Cty đang cần 200 tu nghiệp sinh cho c&aacute;c nghiệp đo&agrave;n tại Nhật Bản, l&agrave;m việc trong nhiều lĩnh vực như: sản xuất phụ t&ugrave;ng &ocirc; t&ocirc; (bọc da, d&aacute;n giả da ghế &ocirc; t&ocirc;), tiện kim loại, đ&uacute;c &ndash; tiện, mạ &ndash; đ&uacute;c&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &Ocirc;ng Vũ C&ocirc;ng B&igrave;nh &ndash; Chủ tịch HĐQT ki&ecirc;m Tổng gi&aacute;m đốc Cty Cổ phần ph&aacute;t triển nguồn nh&acirc;n lực (LOD) cũng cho biết, đang tuyển khoảng 300 lao động sang tu nghiệp tại Nhật Bản trong nhiều lĩnh vực như sửa chữa đ&oacute;ng t&agrave;u, chế biến n&ocirc;ng sản, cơ kh&iacute;. Ngo&agrave;i ra, một số Cty kh&aacute;c cũng đang tuyển lao động đi l&agrave;m việc tại Nhật Bản với số lượng lớn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &Ocirc;ng Nguyễn Ngọc Quỳnh &ndash; Cục trưởng Quản l&yacute; lao động ngo&agrave;i nước cho biết, Nhật Bản l&agrave; thị trường tốt, cho thu nhập cao. Thực tế, trong tổng số 14 nước c&oacute; TNS đang l&agrave;m việc tại Nhật Bản, số lượng TNS Việt Nam xếp thứ hai sau Trung Quốc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; Điều kiện để được đi Nhật l&agrave; nam- nữ, tuổi từ 19-30, c&oacute; sức khỏe tốt. Người tham gia đơn h&agrave;ng đi Nhật bắt buộc phải c&oacute; bằng tốt nghiệp THPT hoặc tương đương. Ưu ti&ecirc;n những người đ&atilde; c&oacute; tay nghề. Chi ph&iacute; đi Nhật (t&ugrave;y theo quy định của c&aacute;c Cty) nhưng trung b&igrave;nh l&agrave; 8.000 USD. Thời gian l&agrave;m việc 3 năm.</p>\r\n\r\n<p><br />\r\n<em><strong>Ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng phục vụ Qu&yacute; phụ huynh v&agrave; học sinh!</strong></em><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Mọi chi tiết xin vui l&ograve;ng li&ecirc;n hệ</strong><strong>:</strong><br />\r\n<strong>TẬP ĐO&Agrave;N KẾ TO&Aacute;N H&Agrave; NỘI</strong><br />\r\n<strong>C&Ocirc;NG TY CỔ PHẦN TƯ VẤN DU HỌC KOKONO</strong></p>\r\n\r\n<p><strong>Địa chỉ: Số 4 ng&otilde; 322, L&ecirc; Trọng Tấn, Thanh Xu&acirc;n, H&agrave; Nội</strong></p>\r\n\r\n<p><strong>Tel: 043 3945 999 &nbsp; &nbsp;Hotline: 0913 828 222 - 0989 212 668</strong><br />\r\n<strong>Email: duhockokono@gmail.com</strong><br />\r\n<strong>Website: www.duhockokono.vn</strong></p>\r\n', ''),
(11, 3, 'TU NGHIỆP SINH NHẬT BẢN, NGUỒN NHÂN LỰC BỊ LÃNG PHÍ', '<p>Với những trải nghiệm ở Nhật Bản, tu nghiệp sinh (TNS) trở th&agrave;nh những người c&oacute; sự pha trộn văn h&oacute;a cũ v&agrave; mới, v&agrave; khi trở về họ kh&aacute;c với l&uacute;c c&ograve;n ở qu&ecirc; nh&agrave; cũng như l&uacute;c đang lao động tại Nhật. Ch&iacute;nh đặc điểm &ldquo;thể lai&rdquo; n&agrave;y l&agrave;m cho họ trở th&agrave;nh những nh&oacute;m c&oacute; &ldquo;vốn x&atilde; hội&rdquo; ri&ecirc;ng v&agrave; c&oacute; sự kh&aacute;c biệt trong chiến lược t&igrave;m kiếm tương lai. Tiếc l&agrave; nguồn vốn n&agrave;y đang bị l&atilde;ng ph&iacute;.<br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; Theo thống k&ecirc; chưa ch&iacute;nh thức, từ năm 2000 đến nay, c&oacute; khoảng 40.000 tu nghiệp sinh l&agrave; lao động phổ th&ocirc;ng (tốt nghiệp THPT) v&agrave; kỹ sư (tốt nghiệp đại học) đến Nhật Bản. Đ&aacute;ng kể, từ sau năm 2010, với việc thay đổi ch&iacute;nh s&aacute;ch tuyển dụng lao động của Nhật Bản (trước đ&oacute; Nhật Bản chủ yếu tuyển dụng lao động từ Trung Quốc), số lượng tu nghiệp sinh Việt Nam đến Nhật đ&atilde; tăng l&ecirc;n nhanh, trung b&igrave;nh mỗi năm c&oacute; khoảng 4.000-5.000 người đến Nhật v&agrave; khoảng 2.000 người trở về nước sau khi hết hợp đồng ba năm hoặc một năm.<br />\r\n&nbsp;<br />\r\n&nbsp; &nbsp; C&oacute; kh&aacute; nhiều c&acirc;u hỏi trong t&acirc;m tr&iacute; họ khi trở về: phải sử dụng đồng tiền t&iacute;ch lũy được như thế n&agrave;o? Sẽ l&agrave;m c&ocirc;ng việc g&igrave; v&agrave; ở đ&acirc;u với chuy&ecirc;n m&ocirc;n học được? N&ecirc;n tiếp tục t&igrave;m kiếm c&ocirc;ng việc ở nước ngo&agrave;i hay tạo dựng cuộc sống tại qu&ecirc; nh&agrave;? Sống ở nơi n&agrave;o để c&oacute; tương lai?<br />\r\n&nbsp;<br />\r\n&nbsp; &nbsp;TNS đến Nhật Bản l&agrave;m việc đa ng&agrave;nh nghề, thuộc c&aacute;c lĩnh vực c&ocirc;ng nghiệp, n&ocirc;ng nghiệp, thương mại v&agrave; dịch vụ. Họ được c&aacute;c doanh nghiệp Nhật Bản tuyển dụng theo nhu cầu v&agrave; th&ocirc;ng qua sự tuyển chọn của c&aacute;c c&ocirc;ng ty tại Việt Nam, đến Nhật l&agrave;m việc tại nhiều doanh nghiệp nhỏ v&agrave; vừa trong nền kinh tế Nhật (*). Mỗi tu nghiệp sinh đảm nhận một c&ocirc;ng việc trong một d&acirc;y chuyển sản xuất hiện đại, kh&eacute;p k&iacute;n v&agrave; với c&aacute;c sản phẩm ri&ecirc;ng.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"Tu nghiệp sinh Nhật Bản được đào tạo rất khắt khe\" src=\"http://duhockokono.vn/pic/Service/images/636120503779132814.jpg.ashx\" /><br />\r\n&nbsp;</p>\r\n\r\n<p><strong><em>Tu nghệp sinh Nhật Bản phải đ&agrave;o tạo mất rất nhiều thời gian</em></strong></p>\r\n\r\n<p>&nbsp;<br />\r\n&nbsp; &nbsp; Ở Nhật, c&aacute;c tu nghiệp sinh được trải nghiệm nền sản xuất hiện đại v&agrave; chuy&ecirc;n m&ocirc;n h&oacute;a cao. Họ c&oacute; m&ocirc;i trường rất tốt để thực h&agrave;nh t&iacute;nh kỷ luật, sự chăm chỉ v&agrave; tr&aacute;ch nhiệm trong c&ocirc;ng việc. Họ ra đi v&agrave; trở về với tinh thần cầu tiến, ham muốn được l&agrave;m việc hết m&igrave;nh. Họ l&agrave; nguồn nh&acirc;n lực trẻ đ&atilde; qua lao động, chủ yếu trong độ tuổi 25-30, c&oacute; cả nam lẫn nữ v&agrave; c&oacute; bản sắc văn h&oacute;a ri&ecirc;ng. Thế nhưng, khi trở về nước, kh&ocirc;ng c&oacute; những doanh nghiệp &ldquo;đ&oacute;n nhận&rdquo; họ. Tu nghiệp sinh kh&ocirc;ng dễ tiếp cận được nơi l&agrave;m việc &ldquo;tương tự&rdquo;, m&agrave; thường gặp phải những kh&aacute;c biệt qu&aacute; lớn về c&ocirc;ng nghệ sản xuất, c&aacute;ch quản l&yacute; v&agrave; ph&acirc;n c&ocirc;ng lao động, về tiền lương v&agrave; ph&uacute;c lợi... Từ đ&oacute;, những kỹ thuật chuy&ecirc;n m&ocirc;n v&agrave; niềm đam m&ecirc; lao động cũng phai nhạt theo năm th&aacute;ng.<br />\r\n&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;Theo chương tr&igrave;nh tu nghiệp sinh, c&aacute;c doanh nghiệp đ&atilde; ưu ti&ecirc;n tuyển dụng lao động tại c&aacute;c địa phương c&oacute; điều kiện kinh tế kh&oacute; khăn, nơi c&oacute; nhiều gia đ&igrave;nh thuộc diện ngh&egrave;o, ch&iacute;nh s&aacute;ch (do chiến tranh). Theo đ&oacute;, c&aacute;c địa phương như Củ Chi (TPHCM), Bến Tre, Tiền Giang, T&acirc;y Ninh, Quảng Nam, Quảng Ng&atilde;i, Thanh H&oacute;a... l&agrave; những nơi c&oacute; nhiều tu nghiệp sinh ra đi v&agrave; trở về. Đến nay, phần lớn tu nghiệp sinh đ&atilde; trở lại c&aacute;c v&ugrave;ng n&ocirc;ng th&ocirc;n hoặc ph&acirc;n t&aacute;n &ldquo;như chim vỡ tổ&rdquo; tại c&aacute;c th&agrave;nh phố lớn để t&igrave;m kiếm sinh kế mới. Nguồn nh&acirc;n lực n&agrave;y kh&ocirc;ng được tập trung v&agrave; sự kết nối của ch&iacute;nh họ để &ldquo;gi&uacute;p nhau&rdquo; cũng dần mất đi. Những gi&aacute; trị v&agrave; chuy&ecirc;n m&ocirc;n học được của c&aacute;c tu nghiệp sinh từ nền sản xuất c&ocirc;ng nghiệp, văn h&oacute;a v&agrave; văn minh Nhật Bản, được kỳ vọng l&agrave; sẽ đ&oacute;ng g&oacute;p cho qu&aacute; tr&igrave;nh c&ocirc;ng nghiệp h&oacute;a-hiện đại h&oacute;a ở Việt Nam đ&atilde; bị ph&acirc;n t&aacute;n. Thực tế, trong số h&agrave;ng chục ng&agrave;n tu nghiệp sinh trở về, c&oacute; nhiều người đ&atilde; trở lại với cuộc sống &ldquo;an nh&agrave;n&rdquo; v&agrave; &ldquo;nội trợ&rdquo;, c&oacute; người y&ecirc;u tiếng Nhật, văn h&oacute;a Nhật th&igrave; t&igrave;m kiếm việc l&agrave;m trong c&aacute;c nh&agrave; h&agrave;ng Nhật ở c&aacute;c th&agrave;nh phố v&agrave; cũng kh&ocirc;ng &iacute;t tu nghiệp sinh lại ra đi t&igrave;m việc tại c&aacute;c quốc gia kh&aacute;c chưa hẹn ng&agrave;y về.<br />\r\n&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;C&oacute; thể n&oacute;i, Ch&iacute;nh phủ chưa c&oacute; chiến lược d&agrave;i hạn mang tầm quốc gia cho việc sử dụng hiệu quả &ldquo;nguồn nh&acirc;n lực trở về&rdquo;. Từ đ&oacute;, kh&ocirc;ng &iacute;t tu nghiệp sinh ngay khi c&ograve;n &ldquo;tu nghiệp&rdquo; ở Nhật Bản đ&atilde; t&iacute;ch lũy đồng lương chỉ để mua đất đai v&agrave; nh&agrave; ở, nhằm đặt chỗ cho sự trở về. Đ&oacute; l&agrave; c&aacute;ch t&iacute;ch lũy vốn v&agrave; cũng l&agrave; một chiến lược an cư để t&igrave;m sự lạc nghiệp theo c&aacute;ch của họ. Chỉ mới gần đ&acirc;y, khi c&aacute;c doanh nghiệp nhỏ v&agrave; vừa Nhật Bản tỏ &yacute; sẽ mở nhiều hơn c&aacute;c chi nh&aacute;nh sản xuất tại Việt Nam, c&aacute;c TNS trở về mới c&oacute; th&ecirc;m những cơ hội v&agrave; hy vọng. Chưa c&oacute; lối đi ri&ecirc;ng chắc chắn v&agrave; cũng kh&ocirc;ng c&oacute; nhiều ng&atilde; rẽ cho những tu nghiệp sinh trở về tạo dựng sự nghiệp bằng ch&iacute;nh chuy&ecirc;n m&ocirc;n học được của m&igrave;nh. Phần nhiều tu nghiệp sinh chưa c&oacute; điều kiện để đ&oacute;ng g&oacute;p từ &ldquo;vốn x&atilde; hội&rdquo; của m&igrave;nh v&agrave;o nền kinh tế - x&atilde; hội Việt Nam. Trong c&acirc;u chuyện n&agrave;y, ch&uacute;ng t&ocirc;i thấy tiếc nuối v&agrave; lo lắng, đặc biệt khi Việt Nam đang l&agrave; quốc gia c&oacute; năng suất lao động thấp nhất trong khu vực.<br />\r\n&nbsp;(*) Theo chương tr&igrave;nh tu nghiệp sửa đổi, c&oacute; hiệu lực từ ng&agrave;y 1-7-2010, tổng số ng&agrave;nh nghề v&agrave; loại h&igrave;nh c&ocirc;ng việc d&agrave;nh cho chương tr&igrave;nh tu nghiệp sinh (thực tập sinh kỹ năng) l&agrave; 64 ng&agrave;nh nghề v&agrave; 120 loại h&igrave;nh c&ocirc;ng việc (trước năm 2010 c&oacute; 63 ng&agrave;nh nghề v&agrave; 116 loại h&igrave;nh c&ocirc;ng việc).<br />\r\n&nbsp;<br />\r\n&nbsp; &nbsp; Phạm Thanh Th&ocirc;i - Ng&ocirc; Thị Phương Lan<br />\r\n&nbsp; &nbsp; Theo B&aacute;o Kinh tế S&agrave;i G&ograve;n Online</p>\r\n', ''),
(12, 3, 'Hỏi: Có khó khăn gì khi xin việc làm chính thức tại Nhật Bản sau khi tôi tốt nghiệp từ bậc trung cấp trở lên tại Nhật Bản?', '<p><br />\r\n<img alt=\"\" src=\"http://duhockokono.vn/pic/Service/images/hoi-dap-du-hoc-nhat-ban.png\" /><br />\r\n<strong>&nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;Kokono trả lời:</strong>&nbsp;Nhật Bản l&agrave; đất nước c&oacute; &ldquo;d&acirc;n số gi&agrave;&rdquo;. Do đ&oacute; họ lu&ocirc;n khuyến kh&iacute;ch những bạn trẻ ở lại l&agrave;m việc. Tất nhi&ecirc;n mức lương m&agrave; họ trả cho bạn cũng chỉ bằng 70% họ trả cho người Nhật. V&iacute; dụ lương cơ bản của người Nhật l&agrave; 80 triệu/ 1 th&aacute;ng th&igrave; lương cơ bản của bạn khoảng 56 triệu/ 1 th&aacute;ng. Do đ&oacute; cơ hội việc l&agrave;m đối với bạn l&agrave; rất lớn. Theo thống k&ecirc; của Trường Nhật ngữ th&igrave; c&oacute; đến 98% du học sinh&nbsp;Việt Nam ở lại Nhật Bản&nbsp;l&agrave;m việc sau khi tốt nghiệp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>Ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng phục vụ Qu&yacute; phụ huynh v&agrave; học sinh!</strong></em><br />\r\n<br />\r\n<strong>Mọi chi tiết xin vui l&ograve;ng li&ecirc;n hệ:</strong><br />\r\n<strong>TẬP ĐO&Agrave;N KẾ TO&Aacute;N H&Agrave; NỘI</strong><br />\r\n<strong>C&Ocirc;NG TY CỔ PHẦN TƯ VẤN DU HỌC KOKONO</strong></p>\r\n\r\n<p><strong>Địa chỉ: Số 4 ng&otilde; 322, L&ecirc; Trọng Tấn, Thanh Xu&acirc;n, H&agrave; Nội</strong></p>\r\n\r\n<p><strong>Tel: 043 3945 999 &nbsp; &nbsp;Hotline: 0913 828 222 - 0989 212 668</strong><br />\r\n<strong>Email: duhockokono@gmail.com</strong><br />\r\n<strong>Website:&nbsp;<a href=\"http://duhockokono.vn/\">www.duhockokono.vn</a></strong></p>\r\n', ''),
(13, 4, 'LỚP HỌC TIẾNG NHẬT TẠI HÀ NỘI CHO NGƯỜI MỚI BẮT ĐẦU - BẤT NGỜ VỚI CHƯƠNG TRÌNH ƯU ĐÃI LỚN!', '<p>&nbsp;<strong>Lớp học Tiếng Nhật ở H&agrave; Nội&nbsp;đảm bảo chất lượng đặt l&ecirc;n h&agrave;ng đầu. Ngay cả với những bạn mới bắt đầu l&agrave;m quen với Tiếng Nhật th&igrave; chỉ sau 01 KHO&Aacute; HỌC C&Oacute; THỂ ĐẠT N5.</strong><strong>&nbsp;Ng&ocirc;n ngữ được cho l&agrave; kh&oacute; nhằn n&agrave;y đ&atilde; được đội ngũ c&aacute;c thầy c&ocirc; Kokono mang những điều tinh t&uacute;y nhất đến H&agrave; Nội, d&agrave;nh cho những bạn trẻ c&oacute; niềm đam m&ecirc; mong muốn chinh phục n&oacute;.&nbsp;</strong><strong>C&oacute; đến 10 chi nh&aacute;nh lớp học Tiếng Nhật Kokono tại H&agrave; Nội tha hồ cho bạn lựa chọn:&nbsp;</strong><strong><em>Thanh Xu&acirc;n, Cầu Giấy, Đống Đa, Hai B&agrave; Trưng, Long Bi&ecirc;n, H&agrave; Đ&ocirc;ng, Thanh Tr&igrave;, Đ&ocirc;ng Anh, Gia L&acirc;m, Sơn T&acirc;y.&nbsp;</em></strong><strong>C&ugrave;ng t&igrave;m hiểu xem c&oacute; những g&igrave; tại lớp học Tiếng Nhật H&agrave; Nội &quot;si&ecirc;u chất&quot; n&agrave;y nh&eacute;!&nbsp;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://duhockokono.vn/103-kanji-n5-tieng-nhat-hoc-kanji-bang-hinh-anh.htm\" target=\"_blank\"><strong>&gt;&gt; C&aacute;ch học 103 Kanji N5 Tiếng Nhật - Học Kanji bằng h&igrave;nh ảnh</strong></a></p>\r\n\r\n<p><img alt=\"\" src=\"http://duhockokono.vn/pic/New/images/Hoc-tieng-nhat-cung-Kokono-6.gif\" style=\"height:253px; width:450px\" /></p>\r\n\r\n<p><br />\r\n<strong>05 ưu thế vượt trội của lớp học tiếng Nhật ở H&agrave; Nội:&nbsp;</strong><em>Chương tr&igrave;nh b&aacute;m s&aacute;t thực tế.&nbsp;</em><em>Giảng vi&ecirc;n từng sinh sống v&agrave; l&agrave;m việc tại Nhật. Thời lượng mỗi tiết học 2h/buổi, 3 buổi/tuần. Mỗi lớp chỉ từ 3-5 học vi&ecirc;n.&nbsp;</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Lớp học tiếng nhật hà nội Thanh Xuân, Cầu Giấy, Đống Đa, Hai Bà Trưng, Long Biên, Hà Đông, Thanh Trì, Đông Anh, Gia Lâm, Sơn Tây\" src=\"http://duhockokono.vn/pic/New/images/Kokono-hoc-tieng-nhat-trung-han.png\" style=\"height:20px; width:20px\" />&nbsp;<strong>Lớp học Tiếng Nhật tại H&agrave; Nội phục vụ mục đ&iacute;ch:</strong>&nbsp;<strong><em>&quot;Cấp tốc - Thực tế&quot;, lớp học&nbsp;&nbsp;</em>Kokono H&agrave; Nội</strong>&nbsp;mong muốn đưa Tiếng Nhật trở n&ecirc;n gần gũi v&agrave; hữu &iacute;ch nhất trong cuộc sống h&agrave;ng ng&agrave;y của bạn. Điều đ&oacute; thể hiện qua chương tr&igrave;nh học, thời lượng, nguồn kiến thức v&agrave; c&aacute;ch lựa chọn giảng vi&ecirc;n. Để đảm bảo mục ti&ecirc;u ho&agrave;n th&agrave;nh<strong>&nbsp;20 - 25 b&agrave;i Minna no Nihongo</strong>, Kokono đ&atilde; tăng thời lượng mỗi buổi l&ecirc;n<strong>&nbsp;</strong>2h, giảm quy m&ocirc; lớp từ<strong>&nbsp;</strong>5 - 7&nbsp;người&nbsp;để sao s&aacute;t đến từng học vi&ecirc;n. Nếu như trước kia, bạn phải học 3 - 4 kh&oacute;a mới ho&agrave;n th&agrave;nh được chương tr&igrave;nh n&agrave;y, th&igrave; hiện nay,&nbsp;<strong><em>lớp học Tiếng Nhật tại H&agrave; Nội&nbsp;</em></strong><em>chỉ với&nbsp;01 kh&oacute;a học để đạt N5&nbsp;ch&iacute;nh l&agrave; lựa chọn chất lượng, tiết kiệm thời gian cho bạn.</em></p>\r\n\r\n<h2>&gt;&gt; LỚP HỌC&nbsp;<em>TIẾNG NHẬT CẤP TỐC&nbsp;</em>CHẤT LƯỢNG CAO &lt;&lt;</h2>\r\n\r\n<p><strong>[ QUY M&Ocirc; LỚP CHỈ TỪ 5 - 7 NGƯỜI ]<br />\r\n&nbsp;</strong><br />\r\n<strong>!!! TƯNG BỪNG ƯU Đ&Atilde;I C&Ugrave;NG KOKONO</strong><strong>&nbsp;</strong><strong><img alt=\"Khóa học Tiếng Nhật Giao tiếp cấp tốc\" src=\"http://duhockokono.vn/pic/New/images/Khoa%20hoc%20Ke%20toan%20tong%20hop%20thuc%20hanh%20thuc%20te%20-%20Ke%20toan%20Ha%20Noi%2003.gif\" style=\"height:22px; width:41px\" /><br />\r\n&nbsp;</strong></p>\r\n\r\n<p><strong>Giảm&nbsp;30%</strong>&nbsp;cho học vi&ecirc;n&nbsp;<strong><em>đăng k&yacute; 02&nbsp;</em></strong><strong><em>Kh&oacute;a</em></strong><strong><em>&nbsp;học Tiếng Nhật li&ecirc;n tục</em></strong>&nbsp;tr&ecirc;n to&agrave;n Hệ thống Kokono<br />\r\n&nbsp;<br />\r\n<strong>Giảm&nbsp;20%</strong>&nbsp;cho học vi&ecirc;n đăng k&yacute; Kh&oacute;a học Tiếng Nhật tại Hệ thống&nbsp;<strong><em>Kokono H&agrave; Nội v&agrave; Tp. HCM</em></strong><br />\r\n&nbsp;<br />\r\n<strong>Giảm&nbsp;10%</strong>&nbsp;cho học vi&ecirc;n đăng k&yacute;&nbsp;Kh&oacute;a&nbsp;học Tiếng Nhật tại Hệ thống&nbsp;<strong><em>Kokono tại c&aacute;c Tỉnh<br />\r\n&nbsp;</em></strong><br />\r\n<strong>Giảm&nbsp;TH&Ecirc;M&nbsp;5%</strong>&nbsp;khi Đăng k&yacute;<strong><em>&nbsp;nh&oacute;m 2 người trở l&ecirc;n&nbsp;</em></strong>tr&ecirc;n to&agrave;n Hệ thống.<br />\r\n<br />\r\n<em>Đ&acirc;y l&agrave; cơ hội tuyệt vời cho những ai muốn học Tiếng Nhật cấp tốc với mức học ph&iacute; tốt nhất! Nhanh tay đăng k&yacute; nhận ưu đ&atilde;i qua hotline! Lớp học tiếng Nhật n&agrave;y ch&iacute;nh l&agrave; d&agrave;nh cho bạn!<br />\r\n&nbsp;</em></p>\r\n\r\n<table align=\"center\" style=\"width:756px\">\r\n	<caption><strong>BẢNG ƯU Đ&Atilde;I HỌC PH&Iacute;<br />\r\n	&nbsp;</strong></caption>\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"text-align:center\">Đối tượng ưu đ&atilde;i</th>\r\n			<th scope=\"col\" style=\"text-align:center\">Học ph&iacute; chưa giảm</th>\r\n			<th scope=\"col\" style=\"text-align:center\">Học ph&iacute;<br />\r\n			&nbsp;<br />\r\n			<em>Đ&Atilde;&nbsp;GIẢM</em>&nbsp;chỉ c&ograve;n</th>\r\n			<th scope=\"col\" style=\"text-align:center\">Học ph&iacute; ĐK<br />\r\n			&nbsp;<br />\r\n			Nh&oacute;m 2 người trở l&ecirc;n</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong><em>&nbsp;<br />\r\n			Lớp học&nbsp;ở H&agrave; Nội &amp; TP.HCM<br />\r\n			&nbsp;</em></strong></td>\r\n			<td style=\"text-align:center\">&nbsp;<br />\r\n			5.000.000đ<br />\r\n			&nbsp;</td>\r\n			<td style=\"text-align:center\"><br />\r\n			<em><strong>4.000.000đ</strong></em><br />\r\n			&nbsp;</td>\r\n			<td style=\"text-align:center\"><br />\r\n			<em><strong>3.750.000đ</strong></em><br />\r\n			&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><br />\r\n			<strong>Lớp học ở Chi nh&aacute;nh c&aacute;c Tỉnh</strong><br />\r\n			&nbsp;</td>\r\n			<td style=\"text-align:center\"><br />\r\n			5.000.000đ<br />\r\n			&nbsp;</td>\r\n			<td style=\"text-align:center\"><br />\r\n			<strong>4.500.000đ</strong><br />\r\n			&nbsp;</td>\r\n			<td style=\"text-align:center\"><br />\r\n			<strong><em>4.250.000đ</em></strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong><em>&nbsp;</em></strong></p>\r\n\r\n<p><em><strong>=&gt;&gt; Đăng k&yacute; nhận ưu đ&atilde;i ngay tại Hotline:&nbsp;0913.828.222 - 0989.212.668<br />\r\n&nbsp;</strong></em></p>\r\n\r\n<p><strong><a href=\"https://docs.google.com/forms/d/e/1FAIpQLSfwGIDJ0sV6CgOrK5Fa4RAf14E_gQn0T8hcTnAMZaiP3e2wvQ/viewform\" target=\"_blank\"><img alt=\"Đăng ký ưu đãi khóa học tiếng - Kokono \" src=\"http://duhockokono.vn/pic/New/images/nhan-uu-dai-khoa-hoc-tieng-Kokono-2(1).gif\" style=\"height:105px; width:260px\" /></a></strong></p>\r\n\r\n<p><br />\r\n<strong>(*) C&aacute;c ưu đ&atilde;i đi k&egrave;m:</strong><br />\r\n- Hỗ trợ tối đa để thực h&agrave;nh tiếng;<br />\r\n- Được&nbsp;<strong><em>học lại 01 kh&oacute;a miễn ph&iacute;&nbsp;</em></strong>nếu chưa nắm chắc kiến thức kh&oacute;a đ&oacute;;<br />\r\n-&nbsp;<em><strong>100% học vi&ecirc;n được&nbsp;</strong></em><em><strong><a href=\"http://duhockokono.vn/tuyen-sinh-du-hoc-tai-nhat-ban-ky-thang-10-nam-2017.htm\" target=\"_blank\">tư vấn Du Học Nhật Bản miễn ph&iacute;</a></strong></em><br />\r\n- Được chia sẻ kinh nghiệm thực tế cuộc sống tại Nhật;<br />\r\n<br />\r\n<em><strong>Th&agrave;nh c&ocirc;ng chỉ đến với những người d&aacute;m h&agrave;nh động!&nbsp;</strong></em>Với những ưu đ&atilde;i đến tận c&ugrave;ng tr&ecirc;n đ&acirc;y, c&ograve;n chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng đăng k&yacute;&nbsp;<em><strong>học tiếng Nhật cấp tốc</strong></em>&nbsp;ngay tại hotline:&nbsp;<em><strong>0913.828.222 - 0989.212.668.&nbsp;</strong></em>Cơ hội chinh phục Tiếng Nhật ch&iacute;nh l&agrave; của bạn!</p>\r\n\r\n<h3>Đến Kokono H&agrave; Nội - Học tiếng Nhật l&agrave; phải n&oacute;i được Tiếng Nhật!</h3>\r\n', '');
INSERT INTO `post` (`id`, `cate_id`, `title`, `content`, `description`) VALUES
(14, 4, 'Cách học 103 Kanji N5 Tiếng Nhật - Minh họa hình ảnh cực dễ nhớ', '<p><strong><a href=\"http://duhockokono.vn/103-kanji-n5-tieng-nhat-hoc-kanji-bang-hinh-anh.htm\">C&aacute;ch học 103 Kanji N5 tiếng Nhật</a>&nbsp;dễ nhớ nhất. Dưới đ&acirc;y, Kokono sẽ giới thiệu c&aacute;ch học từng Kanji một c&aacute;ch nhanh &quot;thấm&quot; nhất - Học Kanji bằng h&igrave;nh ảnh. B&ecirc;n cạnh đ&oacute; l&agrave; video hướng dẫn chi tiết c&aacute;ch đọc từng Kanji. Click v&agrave;o mỗi Kanji để xem chi tiết c&aacute;ch học, c&aacute;ch viết v&agrave; c&aacute;ch nhớ của từng Kanji nh&eacute;! C&ugrave;ng Kokono chinh phục Kanji thật dễ d&agrave;ng!!</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><a href=\"http://duhockokono.vn/5-uu-the-khoa-hoc-tieng-nhat-tieng-trung-tieng-han-online.htm\" target=\"_blank\"><strong>TRẢI NGHIỆM HỌC TIẾNG NHẬT CỰC MỚI&nbsp;</strong><strong>&gt;&gt; Xem thử &lt;&lt;</strong></a></p>\r\n\r\n<p><br />\r\n<strong><a href=\"http://duhockokono.vn/lop-hoc-tieng-nhat-ha-noi.htm\" target=\"_blank\">&gt;&gt;&nbsp;</a><a href=\"http://duhockokono.vn/lop-hoc-tieng-nhat-ha-noi.htm\" target=\"_blank\">Cập nhật Ưu đ&atilde;i mới nhất của Lớp học tiếng Nhật tại H&agrave; Nội&nbsp;</a></strong><strong><a href=\"http://duhockokono.vn/lop-hoc-tieng-nhat-ha-noi.htm\" target=\"_blank\">&lt;mới&gt;</a></strong><br />\r\n<br />\r\n<strong><a href=\"http://duhockokono.vn/tai-lieu-tong-hop-danh-sach-103-kanji-n5-pdf.htm\" target=\"_blank\">&gt;&gt; Tổng hợp 103 Kanji N5 (* file PDF)</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><img alt=\"Học tiếng Nhật Kokono:\" src=\"http://duhockokono.vn/pic/New/images/Chuong-gif(4).gif\" />&nbsp;C&aacute;ch Tự Học 103 Kanji N5 Tiếng Nhật như sau:</h3>\r\n\r\n<p><strong>Bước 1: Hiểu nghĩa Kanji:</strong><br />\r\nClick v&agrave;o từng Kanji để hiểu &yacute; nghĩa chiết tự, giải nghĩa chữ H&aacute;n v&agrave; học Kanji theo h&igrave;nh ảnh.<br />\r\n<br />\r\n<strong>Bước 2: Học viết Kanji:</strong><br />\r\nHọc viết theo ảnh c&aacute;ch viết của từng Kanji trong mỗi b&agrave;i.<br />\r\n<br />\r\n<strong>Bước 3: Nghe lượt đầu:</strong><br />\r\nSau khi nhớ được &yacute; nghĩa v&agrave; c&aacute;ch viết c&aacute;c Kanji, mở file video để nghe 1 lượt/Kanji.<br />\r\n<br />\r\n<strong>Bước 4: Nghe v&agrave; nhắc lại:</strong><br />\r\nNghe v&agrave; đọc lại theo từng Kanji 5 lần/Kanji<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Bước 5: Nghe kh&ocirc;ng nh&igrave;n chữ v&agrave; viết lại:</strong><br />\r\nChỉ nghe, kh&ocirc;ng nh&igrave;n chữ tr&ecirc;n video.&nbsp;<em>Viết lại ra giấy những chữ nghe được 5 lần/Kanji&nbsp;(<strong>*Bước n&agrave;y v&ocirc; c&ugrave;ng quan trọng - Kh&ocirc;ng được bỏ qua bước n&agrave;y!)</strong></em></p>\r\n\r\n<p><strong><em>Video c&aacute;ch ph&aacute;t &acirc;m c&aacute;c Kanji v&agrave; từ vựng li&ecirc;n quan Phần 1 (Kanji 1 -30):</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<em>&quot;Sự ki&ecirc;n nhẫn đắng ch&aacute;t, nhưng quả của n&oacute; lại thật ngọt ng&agrave;o&quot;</em>. Học Tiếng Nhật kh&ocirc;ng phải ng&agrave;y một ng&agrave;y hai. Nhưng Kokono tin rằng, với c&aacute;ch học tr&ecirc;n, 103 Kanji N5 nằm trong tầm tay bạn. Đ&acirc;y l&agrave; những Kanji th&ocirc;ng dụng v&agrave; sử dụng rất nhiều trong việc giao tiếp tiếng Nhật h&agrave;ng ng&agrave;y. V&igrave; vậy, h&atilde;y x&acirc;y dựng nền m&oacute;ng thật vững chắc để chinh phục tiếng Nhật th&agrave;nh c&ocirc;ng trong tương lai. Kokono lu&ocirc;n đồng h&agrave;nh với bạn!&nbsp;<br />\r\n<br />\r\n<strong>&gt;&gt; Theo d&otilde;i b&agrave;i học tiếng Nhật qua Fanpage:&nbsp;<a href=\"https://www.facebook.com/H%E1%BB%8Dc-Ti%E1%BA%BFng-Nh%E1%BA%ADt-c%C3%B9ng-Kokono-1202764553162648/\" target=\"_blank\">Học Tiếng Nhật c&ugrave;ng Kokono</a></strong></p>\r\n\r\n<h3>Danh s&aacute;ch 103 Kanji N5 th&ocirc;ng dụng:</h3>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:680px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>STT</strong></td>\r\n			<td><strong>Kanji</strong></td>\r\n			<td><strong>&Acirc;m h&aacute;n việt</strong></td>\r\n			<td><strong>Nghĩa</strong></td>\r\n			<td><strong>&Acirc;m On</strong></td>\r\n			<td><strong>&Acirc;m Kun</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>1</td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhat-mat-troi.htm\" target=\"_blank\">日</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhat-mat-troi.htm\" target=\"_blank\">nhật</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhat-mat-troi.htm\" target=\"_blank\">mặt trời, ng&agrave;y</a></td>\r\n			<td>ニチ, ジツ<br />\r\n			nichi, jitsu</td>\r\n			<td>ひ<br />\r\n			hi, bi</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2</td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhat-mot-dong-nhat.htm\" target=\"_blank\">一</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhat-mot-dong-nhat.htm\" target=\"_blank\">nhất</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhat-mot-dong-nhat.htm\" target=\"_blank\">một; đồng nhất</a></td>\r\n			<td>イチ, イツ<br />\r\n			ichi, itsu</td>\r\n			<td>ひと-<br />\r\n			hito</td>\r\n		</tr>\r\n		<tr>\r\n			<td>3</td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-quoc-dat-nuoc-quoc-gia.htm\" target=\"_blank\">国</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-quoc-dat-nuoc-quoc-gia.htm\" target=\"_blank\">quốc</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-quoc-dat-nuoc-quoc-gia.htm\" target=\"_blank\">nước; quốc gia</a></td>\r\n			<td>コク<br />\r\n			koku</td>\r\n			<td>くに<br />\r\n			kuni</td>\r\n		</tr>\r\n		<tr>\r\n			<td>4</td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhan-nguoi-con-nguoi.htm\" target=\"_blank\">人</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhan-nguoi-con-nguoi.htm\" target=\"_blank\">nh&acirc;n</a></td>\r\n			<td><a href=\"http://duhockokono.vn/kanji-nhan-nguoi-con-nguoi.htm\" target=\"_blank\">nh&acirc;n vật</a></td>\r\n			<td>ジン, ニン<br />\r\n			jin, nin</td>\r\n			<td>ひと<br />\r\n			hito</td>\r\n		</tr>\r\n		<tr>\r\n			<td>5<